-- BIG HEAVY WHITE BOOK WITH BLACK SIGN

require("base.books")

module("items.books.2622_white_book", package.seeall())

-- UPDATE common SET com_script='items.books.2622_white_book' WHERE com_itemid = 2622;

function UseItem(User, SourceItem, TargetItem, Counter, Param)
    if ( TargetItem.id == 266 ) or ( TargetItem.id == 267 ) then
        world:erase(SourceItem,1);
    else
        if base.books.InitBook() then
            base.books.AddLanguage("common language",0);
            base.books.AddLanguage("common language",1);

            base.books.AddGermanBookText("Das Buch der Priester Eldans",0,0,0);
            base.books.AddGermanBookText("Der Novize:",0,0,0);
            base.books.AddGermanBookText("Was du suchst sind Weisheit und Wissen. Doch vielleicht führt dein Weg zu sehr zu Hochmut und nicht zu Eldan.",0,0,0);
            base.books.AddGermanBookText("Sei stark im Glauben. Eldan führt dich und er bringt dich deinem Ziel näher, die Spur zu finden - zur Weisheit.",0,0,0);
            base.books.AddGermanBookText("Sei stark in Taten. Zeige Eifer. Wie dir und mir Eldan den Zweifel nahm, den Hochmut, zeigt er den Weg.",0,0,0);
            base.books.AddGermanBookText("Sei stark im Mute. Ich sehe den Weg. Und werde er steinig oder steil, ihm sind wir bestimmt zu folgen.",0,0,0);

            base.books.AddEnglishBookText("Book of the priests of Eldan",0,0,0);
            base.books.AddEnglishBookText("The novice:",0,0,0);
            base.books.AddEnglishBookText("What you are seeking after is wisdom and knowledge. But maybe your way leads too much to pride but to Eldan.",0,0,0);
            base.books.AddEnglishBookText("Be strong in faith. Eldan will lead you and he will guide you closer to your aim - finding the path of wisdom.",0,0,0);
            base.books.AddEnglishBookText("Be strong in actions. Be zealous. Just as Eldan took our doubt and pride, yours and mine, he will show our way.",0,0,0);
            base.books.AddEnglishBookText("Be strong in courage. I see the way. And though he might become stony or steep, we are meant to follow him.",0,0,0);

            ------------------------------------------------------------------

            base.books.AddGermanBookText("\n \n Die Glaubensrichtungen",0,0,1);
            base.books.AddGermanBookText("~Norodaj~\nVerehrte Götter\n Tanora\n Irmorom\n Malachín\n Zhambra\n \n Die Norodaj verehren hauptsächlich Tanora als Göttin des Wassers und der Seefahrt, sowie Malachín, den heldenhaften Jäger und",0,30,1);
            base.books.AddGermanBookText("Krieger. In der Schöpfungegeschichte der Norodaj heißt es, Tanora habe die Erde aus dem Meer geboren, und Irmorom habe daraufhin Berge herausgeschlagen. Die Felsen, welche er wegschlug, fielen ins",0,30,1);
            base.books.AddGermanBookText("Meer und wurden zu den Inseln. Die Menschen wurden daraufhin vom Meer ans Land gespült. Malachín, der sich dort niedergelassen hatte, brachte ihnen das Jagen bei, doch sie nutzten das Wissen um den",0,30,1);
            base.books.AddGermanBookText("Speer zum Kampfe untereinander und aßen sich gegenseitig. Als Malachín dies sah, fragte er Tanora um Rat : “Die Menschen bekriegen sich. Sie töten einander. Was können wir tun, damit das aufhört?“",0,30,1);
            base.books.AddGermanBookText("Tanora überlegte und gab Malachín ein Boot. Sie schickte ihn auf eine Ferne Insel, dort würde er Rat finden. Als Malachín die Insel erreichte, erblickte er dort seinen Bruder Zhambra, welcher mit",0,30,1);
            base.books.AddGermanBookText("einem Riesen Rang. Auf der Insel selbst stand eine verdorrte Blume, an welcher Früchte hingen. So rief er die Kämpfenden an: „He, Bruder, He, Riese, was tut ihr dort ? Haltet ein und höret auf, euch",0,30,1);
            base.books.AddGermanBookText("wie die Menschen die Köpfe einzuschlagen !“\n Sowohl der Riese als auch Zhambra waren beschämt, mit den Menschen verglichen zu werden, und erröteten. Dann berichtete Zhambra: „Einst pflanzte ich hier",0,30,1);
            base.books.AddGermanBookText("den Samen der Blume, deren zwei Blüten die Namen Freundschaft und Ehre tragen, und vor drei Jahren kehrte ich zurück, um diese zu ernten. Doch dort fand ich den Riesen, und er will nicht, daß ich mein",0,30,1);
            base.books.AddGermanBookText("Werk vollende. Seit drei Jahren ringen wir nun schon, ich bin es müde.“\n Der Riese sprach: „Einst kam ich auf diese Insel, und fand die wundervolle Blume, deren Blüten die Namen Freundschaft und Ehre",0,30,1);
            base.books.AddGermanBookText("tragen. Ich hegte und Pflegte diese Blume, und nun will Zhambra die Blüten brechen! Seit drei Jahren ringen wir nun schon, ich bin es müde.“ Da lachte Irmorom, und zeigte auf die Blume, deren Blüten",0,30,1);
            base.books.AddGermanBookText("sich in Früchte verwandelt hatten. „Seht, die Blüten gibt es nicht mehr. Verwelkt ist die Blume, doch hat sie Früchte geboren, die Samen in sich tragen!“ Da nahm Zhambra die Früchte, gab dem Riesen",0,30,1);
            base.books.AddGermanBookText("zwei der Samen, dankte ihm, und ging mit Malachín zu den Menschen. Auch ihnen gab er Samen der Blüten von Freundschaft und Ehre, und sie hörten auf, sich zu erschlagen. Sie trugen die Samen in die",0,30,1);
            base.books.AddGermanBookText("der Zwerge, die Kinder Irmoroms sind, welcher ein Bruder Malachíns ist, und auch zu den Tempeln der Echsenmenschen.\n Im Glauben der Norodaj werden die Menschen nach ihrem Tode mit einem Boot über",0,30,1);
            base.books.AddGermanBookText("das Meer fahren und nach Ilhagar, der Insel der Ewigkeit suchen, auf welcher die Götter leben.\n Jene, welche gut und ehrenhaft waren, werden die Insel schnell finden. Tanora wird ihnen ruhiges Wasser",0,30,1);
            base.books.AddGermanBookText("Wasser und eine stetige Brise senden. Solche jedoch, welche Feige, niederträchtig und habgierig waren, könnten bei stürmischer See nur karge, von Monstern und Riesen bewohnte Ödlande finden oder gar",0,30,1);
            base.books.AddGermanBookText("von einem Seeungeheuer gefressen werden. Wenn diese Seelen sogar zurück nach Illarion segeln, könnten sie als ruhelose Geister die Sterblichen heimsuchen, bis ein heiliger Mann ihnen sie von ihrem",0,30,1);
            base.books.AddGermanBookText("Schicksal erlöst. Er sendet sie mit einem heiligen Zauber zur Insel der Ewigkeit.\n All jene, welche nicht gut und ehrenhaft gelebt haben, werden nach langer Irrfahrt Ilhagar erreichen, um den",0,30,1);
            base.books.AddGermanBookText("Ehrenhaften in alle Ewigkeit zu dienen oder auch selbst ihre Ehre zurückzuerlangen.\n \n~Serinjah~\nVerehrte Götter\n Malachín\n Cherga\n Sirani\ Ronagan\n  Nargùn\n Elara\n Adron\n \n Das Steppenvolk",0,30,1);
            base.books.AddGermanBookText("verehrt hauptsächlich Malachín, den Gott des Kampfes und der Jagd, sowie die meisten der anderen jüngeren Götter. Die älteren Götter sind ihnen zum Großteil unbekannt, sie nennen sie auch „Die Götter",0,30,1);
            base.books.AddGermanBookText("der Elben und Zauberer“. Moshran wird von ihnen nicht verehrt, sein Name wird generell vermieden, diesen Auszusprechen gilt beinahe als Sakrileg und beschwört schweres Unheil herauf – daher ist dieser",0,30,1);
            base.books.AddGermanBookText("Name nur den Weisen und Schriftgelehrten dieses Volkes bekannt. Die Schöpfungsgeschichte der Serinjah berichtet, daß die Welt von den „Urkräften“ geformt wurde, und diese Urkräfte gebaren auch die",0,30,1);
            base.books.AddGermanBookText("Götter aller Völker zugleich. Die den Serinjah bekannten Götter wachen über das Gleichgewicht zwischen den Urkräften, sie sind die Lehrer der Menschen. Malachin brachte ihnen bei zu kämpfen und zu",0,30,1);
            base.books.AddGermanBookText("Jagen. Ronagan zeigte ihnen, wie man sich an Manku-Hirsche, Kosai-Steppenbüffel und Onashai-Vögel (eine große Laufvogelart, welche auch als Reittier verwendet werden kann) heimlich anschleicht, um",0,30,1);
            base.books.AddGermanBookText("diese lebendig zu fangen, und wie man sich ungesehen zwischen seinen Feinden bewegt. Sirani lehrte sie die hohen Künste des Gedichtes und der Romantik, Elara lehrte sie die Ehre und das Wissen der",0,30,1);
            base.books.AddGermanBookText("Vorfahren. Adron brachte ihnen bei, aus den Körnern des Steppengrases und den Beeren der Sträucher Wein zu keltern. Nargùn zeigte den Menschen ihre eigene Dummheit und brachte ihnen bei, daraus zu",0,30,1);
            base.books.AddGermanBookText("lernen und darüber zu lachen, und Cherga, die Totenwächterin, lehrte sie, die Vorfahren zu ehren und den Ahnen zu opfern.\n Im Glauben des Steppenvolkes reisen die Gestorbenen in die weiten Länder von",0,30,1);
            base.books.AddGermanBookText("Ur, dem Land des ewigen Sommers. Dort gibt es alles im Überfluß, und niemand braucht zu stehlen oder um sein Leben zu fürchten, da man dort nicht Sterben oder verletzt werden kann. Menschen, die zu",0,30,1);
            base.books.AddGermanBookText("Lebzeiten eigennützig waren, haben dazu keinen Grund mehr. Je ehrenhafter ein Mensch jedoch zu Lebzeiten war, umso größer ist sein Ansehen bei den Ahnen.",0,30,1);
            base.books.AddGermanBookText("~Ama-Shoon~\nVerehrte Götter\n Findari\n Sirani\n Cherga\n Elara\n \n Die Ama-Shoon sind kriegerische Gruppen von weiblichen Serinjah. Ihre Mythologie ist die der Steppenvölker, mit dem Unterschied,",0,30,1);
            base.books.AddGermanBookText("daß sie nur zu den weiblichen bekannten Göttern beten. Zudem verehren sie hauptsächlich Findari, welche den Serinjah lediglich als eine „Göttin der Elben“ bekannt ist. Die Ama-Shoon sehen sich selbst",0,30,1);
            base.books.AddGermanBookText("fast als eine Art Ehrengarde des Windes bzw. Findaris an. Sie beschützen das Land und die umliegenden Wälder. Männer sind in ihren Augen von Nargùn berührt, der ihren Geist verwirrt hat, darum können",0,30,1);
            base.books.AddGermanBookText("sie den Göttinnen nicht dienen.\n Wenn eine Ama-Shoon-Kriegerin stirbt, so der Glaube, wird sie von Findari geprüft. Versagt sie oder hat sie im Leben versagt, muß sie den Speer abgeben und bei den ",0,30,1);
            base.books.AddGermanBookText("anderen Serinjah leben.\n \n~Salkamaerer\nVerehrte Götter\n Zhambra\n Oldra\n Elara\n Sirani\n Malachín\n Adron\n Cherga\n Irmorom\n Ronagan\n Nargùn\n \n Die Salkamaerer verehren alle jüngeren",0,30,1);
            base.books.AddGermanBookText("Götter – insbesondere Zhambra, Oldra, Elara, Sirani und Malachin – bis auf Moshran, dessen Name nicht genannt werden darf, denn es bringt großes Unheil, diesen Namen auszusprechen.\n Jedes",0,30,1);
            base.books.AddGermanBookText("Salkamaerische Adelshaus führt seinen Stammbaum auf einen der jüngeren Götter zurück. Die älteren Götter sind unbekannt oder werden einfach „Elben- und Echsengötter“ genannt, andere Religionen werden",0,30,1);
            base.books.AddGermanBookText("höflich toleriert.\n Der Schöpfungsmythos der Salkamaerer besagt, daß die heiligen Götter selbst in der uralten Stadt Salkamar lebten, welche im Nichts schwebte und die damals noch einen Namen in der",0,30,1);
            base.books.AddGermanBookText("Sprache der Götter trug. Um das Nichts tobte elementares Chaos. Da erhob sich Nargùn, der Herr des Chaos, und beschimpfte das Chaos und das Nichts, weil beides ihm nicht gefiel. Er überredete die",0,30,1);
            base.books.AddGermanBookText("anderen Götter, eine ganze Welt zu erschaffen. Und so erschufen die Götter die Welt. Doch die Welt gefiel ihnen, und sie verließen die Stadt und bauten weitere, kleinere Welten, jeder Gott seine",0,30,1);
            base.books.AddGermanBookText("eigene und mehr.\n Doch Cherga sah, daß die Welten, würden sie zusammenstoßen, sich wieder in Chaos verwandeln würden, und so sprach sie : Lasset uns das Nichts zwischen die Welten legen, auf daß es",0,30,1);
            base.books.AddGermanBookText("Illarion von den kleinen Welten trenne und die kleinen Welten voneinander.“ Und Sirani rief : „Und lasset uns alle Welten um Illarion herum befestigen, auf daß man ihre Schönheit von dort aus",0,30,1);
            base.books.AddGermanBookText("bewundern kann!“\n So wurde es getan. Die Welten selbst sind die Sterne, die man am Himmel Illarions sieht. Es sind im Nichts Schwebende Würfel wie Illarion selbst, nur kleiner, und kreisförmig um",0,30,1);
            base.books.AddGermanBookText("Illarion herum angeordnet. Von diesen anderen Welten setzten die Götter später die anderen Völker in die Welt. Und weil die Götter die Welt verlassen hatten und nur noch Priester ihre Macht in ihren",0,30,1);
            base.books.AddGermanBookText("Namen ausüben konnten, wurden die Geschlechter der Menschen sterblich. Nach dem Tode bringt daher Cherga, die Totenwächterin, die Gestorbenen in das Reich des Gottes, der dem Menschen am meisten",0,30,1);
            base.books.AddGermanBookText("bedeutete und zu dem der Mensch am besten passt – meistens ist dies der Hausgott der eigenen Familie oder des Ehegatten / der Ehegattin. War der Gestorbene ehrwürdig und gut, wird er dort ein gutes",0,30,1);
            base.books.AddGermanBookText("Leben haben, war er selbstsüchtig und hinterlistig, erreicht er die Welt seines Gottes nicht, sondern muß ewig durch Chergas graues Nichts wandeln, bis ihm seine Fehler vergeben wurden oder bis",0,30,1);
            base.books.AddGermanBookText("Cherga ihn ergreift und zurück auf Illarion schleudert. Trifft er auf den Boden der Welt, schüttelt der Aufschlag ALLE Erinnerungen aus ihm heraus, und sein Geist wird vollkommen leer im Körper eines",0,30,1);
            base.books.AddGermanBookText("Säuglings wiedergeboren. Fällt er jedoch weich, ins Wasser oder durch ein Blätterdach, kann es passieren, daß er zu einem ruhelosen Geist wird. Heilige Männer und Priester Chergas haben die Aufgabe,",0,30,1);
            base.books.AddGermanBookText("diese Geister zu erlösen, damit Cherga sie erneut werfen kann.\n Die Toten wandeln oft über „Pfade der Welten“ am Himmel und besuchen sich gegenseitig in den Reichen der jeweils anderen Götter. Jedes",0,30,1);
            base.books.AddGermanBookText("Mal, wenn eine Kutsche von einer Welt in die andere Fährt, sehen die Menschen eine Sternschnuppe.\n Manchmal wollen die Vorfahren ihre Kinder beobachten oder ihnen in Träumen wichtige Botschaften",0,30,1);
            base.books.AddGermanBookText("bringen. Dann wird ein glühender Stein an einer Himmelskutsche befestigt, damit sie schwer genug ist, um auf Illarion niedergehen zu können. Ist die Botschaft überbracht, verschwinden Kutsche und",0,30,1);
            base.books.AddGermanBookText("Geist dorthin, woher sie kamen, nur der Stein bleibt zurück.\n \n~Albarianer\nVerehrte Götter\n Adron\n Malachín\n Irmorom\n Zhamra\n Nargùn\n Ronagan\n Moshran\n \n Im Albarischen Reich werden",0,30,1);
            base.books.AddGermanBookText("wohl Aufgrund der dort üblichen patriarchaischen Herrschaftsform, nur männliche Götter verehrt, obwohl eigentlich alle Götter bekannt sind, welche auch in Salkamar angebetet werden.\n Tempel",0,30,1);
            base.books.AddGermanBookText("weiblicher Gottheiten sind im Albarischen Reich verboten, sie anzubeten gilt als schwächlich und als Volksverhetzung. Durchreisende werden oftmals „höflich“ gebeten, ihren Glauben für sich zu",0,30,1);
            base.books.AddGermanBookText("behalten. Wer dem zuwiderhandelt wird, zumeist nach längerem Kerkeraufenthalt und öffentlicher Demütigung sowie Auspeitschung, an die Landesgrenzen gebracht und unter Androhung härterer Strafen",0,30,1);
            base.books.AddGermanBookText("„Peitschentage“, an welchen jeweils mehrere Ausländer, die zu hartnäckig öffentlich ihren Glauben an weibliche Gottheiten vertraten, zur öffentlichen Belustigung ihre dreißig Peitschenhiebe erhalten",0,30,1);
            base.books.AddGermanBookText("nachdem die Bevölkerung sie ausgiebig mit Kot, Mist, faulem Obst und Eiern bewerfen durfte (Damit der Henker und die anwesenden Richter jedoch den Gestank nicht ertragen müssen, wird jeder Gefangene",0,30,1);
            base.books.AddGermanBookText("vor seiner Peitschenstrafe ausgiebig und mit Seife gewaschen).\n Alles in Allem ist die Mythologie Albars dieselbe wie die Salkamaerische Mythologie – mit dem Unterschied, daß weibliche Gottheiten",0,30,1);
            base.books.AddGermanBookText("zwar im Mythos erwähnt werden, ihre Verehrung jedoch verboten ist.\n \n~Gynkeesh~\nVerehrte Götter\n Elara\n Adron\n Oldra\n Cherga\n Malachín\n Irmorom\n Sirani\n Zhambra\n Ronagan\n Nargùn\n Moshran",0,30,1);
            base.books.AddGermanBookText("In Gynk, einer Stadt, deren Häuser auf Hügeln in und Pfählen über einem Sumpf errichtet sind, trafen zwei Kulturen aufeinander : Viele Albarische flüchtige Leibeigene und einige Salkamaerische",0,30,1);
            base.books.AddGermanBookText("geflohene Verbrecher gründeten vor vielen Jahrhunderten eine eigene Kultur und Gesellschaft. Es werden alle jüngeren Götter verehrt, in Gynk gibt es einen Tempel für jeden Gott. Da auch einige Echsen",0,30,1);
            base.books.AddGermanBookText("gund Elben in der Stadt leben, gibt es sogar einen Tempel der Fünf und einen Wassertempel in Gynk – allerdings ist das dem Großteil der Menschlichen Bevölkerung völlig egal.\n Die Mythologie Gynks",0,30,1);
            base.books.AddGermanBookText("entspricht der Salkamars und Albars, es gibt jedoch auch Gynkesen, welche die Religion eines anderen Volkes angenommen haben.\n \n~Schwarzkultisten~\nVerehrte Götter\n Moshran\n Bjolmur\n Dragorog",0,30,1);
            base.books.AddGermanBookText(" Cherass\n \n Schwarzkultisten gibt es aus nahezu jedem Volk (auch wenn es so gut wie niemals vorkommt, daß Halblinge oder gar Elben zu Schwarzkultisten werden).\n Schwarzkultisten sind weithin",0,30,1);
            base.books.AddGermanBookText("berüchtigt und Anhänger oder gar Priester Moshrans oder eines seiner „Kinder“. Sie glauben nur, daß der Stärkere und Schlauere letzten Endes über alle Anderen triumphieren wird. Sie tun nie etwas, das",0,30,1);
            base.books.AddGermanBookText("ihnen selbst in ihren Augen keinen direkten Nutzen einbringt, und erweisen niemals eine Gefälligkeit, da diese als „Güte“ oder „Schwäche“ ausgelegt werden könnte. Oftmals nutzen sie die magischen",0,30,1);
            base.books.AddGermanBookText("Schulen der Nekromantie oder Dämonologie.\n Dieser Glaube ist jedoch in eigentlich allen Ländern verboten, da sie ihr Wissen stets rücksichtslos und ausschließlich zur eigenen Bereicherung anwenden.",0,30,1);
            base.books.AddGermanBookText("Daher arbeiten sie oft unter einem Deckmantel und im Geheimen. Da diese Gruppierungen jedoch dazu tendieren, daß sich die Mitglieder gegenseitig ermorden oder öffentlich denunzieren, um selbst die",0,30,1);
            base.books.AddGermanBookText("Stellung des „aus dem Weg Geräumten“ einzunehmen, bestehen diese Kulte zumeist nur wenige Jahre. Spätestens wenn die Öffentlichkeit oder die örtlichen Gerichtsbarkeiten von ihrem Tun erfahren,",0,30,1);
            base.books.AddGermanBookText("bedeutet dies das Ende ihrer Machenschaften und auch ihres Lebens.\n Die Schöpfungsgeschichte ist ihnen vollkommen egal. Nur der Stärkere wird letzten Endes siegen, und das ist zweifellos Moshran",0,30,1);
            base.books.AddGermanBookText("oder einer seiner drei Unsterblichen. Daher zählt die Vergangenheit nicht. Sie glauben, daß sie nach dem Tode in die Armeen Moshrans aufgenommen werden – wer zu Lebzeiten listig, verschlagen und",0,30,1);
            base.books.AddGermanBookText("unnachgiebig war, wird mit hohen Ehren belohnt oder gar ein wandelnder Leichnahm oder Dämon werden, wer im Leben versagt hat, wird ohne Rang und Ruhm in der ersten Schlachtreihe eines nutzlosen",0,30,1);
            base.books.AddGermanBookText("Konfliktes fallen oder könnte als dienstbarer Geist eines trunksüchtigen Goblins enden.\n \n~Zwerge~\nVerehrte Götter\n Irmorom\n ushara\n Cherga\n \n Die Volksreligion der Zwerge spiegelt die",0,30,1);
            base.books.AddGermanBookText("Zwergischen Traditionen wider. Zwar glauben die Zwerge an die Existenz anderer Götter und sie kommen auch in ihren Mythologien vor, doch kommt es extrem selten vor, daß ein Zwerg seine Erziehung",0,30,1);
            base.books.AddGermanBookText("vergißt und zu einem anderen Gott als Irmorom, Ushara oder Cherga betet. Die anderen Götter haben mit Zwergen nichts zu tun, sie haben – natürlich mit Irmoroms Hilfe – die anderen Völker erschaffen.",0,30,1);
            base.books.AddGermanBookText("Irmorom, auch als „Zwergenvater“ oder „Langbart“ betitelt, gilt als Schöpfer der Zwerge. Er schlug die Zwerge aus dem Fels, und er Schuf in den Augen der Zwerge auch die Knochen der anderen Völker",0,30,1);
            base.books.AddGermanBookText("und Tiere, da die Knochen ja aus Stein bestehen – ihre Leichtigkeit begründet sich nur darin, daß Irmorom so weise war, winzige Löcher hineinzuschlagen, damit sie stabiler und leichter würden. Wer",0,30,1);
            base.books.AddGermanBookText("außer einem Wahrhaftigen Gott wäre dazu fähig, solche Kunstwerke zu vollbringen?\n Es gibt unter den Zwergen mehrere Auslegungen und Philosophien der Geschehnisse während der Schöpfungszeiten, als",0,30,1);
            base.books.AddGermanBookText("Beispiel sei hier die „Offenbarung Tialdins“ aufgeführt. Dabei handelt es sich um eine Zusammenfassung von Abschriften uralter Zwergentexte, welche der König in alten Stollen des legendären siebten",0,30,1);
            base.books.AddGermanBookText("Berges der Zwergenreiche gefunden habe.\n Der Großteil der Zwerge glaubt, daß Irmorom selbst Illarion aus einem Stein vom Hang des Berges Borag-Urubur, dem „Berg des Ursprungs“ erschaffen hat. Ushara",0,30,1);
            base.books.AddGermanBookText("bedeckte auf Irmoroms Bitte den nackten Fels mit Erde, Tanora gab etwas Wasser hinzu und Findari umhüllte alles mit Luft, woraufhin Brágon Illarion mit Feuer wärmte und erhellte.\n Die anderen Götter",0,30,1);
            base.books.AddGermanBookText("Pflanzen und Lebewesen. Als Irmorom die Zwerge erschuf und ihnen steinerne Knochen gab, baten die anderen Götter ihn ebenfalls um steinerne Knochen, um auch stabile Lebewesen daraus machen zu",0,30,1);
            base.books.AddGermanBookText("können. Moshran jedoch bastelte Lebewesen aus Chitin, damit sie einen Panzer hätten, und steckte Nasenschleim hinein, welchen er von Nargùn stahl, als dieser Schnupfen hatte. Diese Wesen dienten",0,30,1);
            base.books.AddGermanBookText("nur dazu, die Zwerge und andere Völker zu ekeln und zu erschrecken und dann und wann auch mal aufzufressen. Irmorom erkannte jedoch Potential in einigen von ihnen, und so segnete er einige dieser",0,30,1);
            base.books.AddGermanBookText("Arten, auf daß sie zu nahrhaften Delikatessen wurden.\n Daraufhin war Moshran allerdings so frustriert, daß er Irmorom ebenfalls um Steinknochen und Brágon um Hilfe bei der Erschaffung der Orks bat,",0,30,1);
            base.books.AddGermanBookText("welche auch heute noch das Potential besitzen, selbst steingeduldige Zwerge zur Weißglut zu bringen und sich absolut nicht als Delikatesse, höchstens jedoch als Zielscheibe eignen.\n Es ist Glaube",0,30,1);
            base.books.AddGermanBookText("der Zwerge, daß sie nach ihrem Tode von Cherga zu einem unendlich riesigen Berg, dem „Borag-Urubur“ geführt werden. Dieser Berg ist das Heim der Götter und so riesig, daß die Welt Illarion selbst",0,30,1);
            base.books.AddGermanBookText("aus einem einzelnen Stein vom Hang des Berges geschlagen wurde.\n Dort warten ihre Ahnen auf sie, sie geleiten sie in die unendlichen Hallen des Berges hinein. Irmorom selbst wird sie dann nach ihrem",0,30,1);
            base.books.AddGermanBookText("Leben befragen, und jeder Zwerg, der wahr spricht und in Ehre und Gerechtigkeit lebte, wird von ihm in die unendlichen Hallen aufgenommen. Jene jedoch, die Lügen und unehrenhaft gelebt haben, werden",0,30,1);
            base.books.AddGermanBookText("in das Land vor den Toren der Hallen verbannt, wo sie ein Dasein als Bauern und Hirten fristen, was eigentlich gar nicht allzu schlimm wäre, wenn die Wälder der Spitzohren (Elben) und die Steppen der",0,30,1);
            base.books.AddGermanBookText("Schweinsnasen (Orks) nicht gleich nebenan lägen. Je ehrenvoller, ruhmreicher und glorreicher das Leben eines Zwergen war, umso glorreicher und ehrenvoller wird sein Nachleben sein.\n Auch ist es",0,30,1);
            base.books.AddGermanBookText("verbreiteter Glaube unter Zwergen, daß Irmorom manchmal einen weisen Zwerg schon zu Lebzeiten mit Wohlstand und loyalem Gefolge belohnen kann.\n \n~Elben~\nVerehrte Götter\n Eldan\n Findari\n Ushara",0,30,1);
            base.books.AddGermanBookText(" Brágon\n Tanora\n Alle jüngeren Götter sind den Elben bekannt. Sie werden verehrt, doch im allgemeinen nicht als Götter. Elben bezeichnen die jüngeren Götter oft als „Heilige Urahnen“.\n Der",0,30,1);
            base.books.AddGermanBookText("traditionelle Glaube der Elben, oft nur als der „Pfad der Suchenden“ bezeichnet, ist für Menschen sehr kompliziert zu begreifen – was auch daran liegen mag, daß die meisten Elben selten mit Angehörigen",0,30,1);
            base.books.AddGermanBookText("anderer Völker Details darüber austauschen. Allerdings bekamen manche Echsen, Menschen und Halblinge, welche sich als sehr tolerant und geduldig sowie wißbegierig erwiesen, einige Abschriften von",0,30,1);
            base.books.AddGermanBookText("religiös-philosophischen Texten der Elben ausgehändigt.\n Alles in allem scheint das Zentrum des „Pfades der Suchenden“ aus Toleranz, persönlicher Entwicklung des Individuums, einem allumfassenden",0,30,1);
            base.books.AddGermanBookText("Meditation und einer grundsätzlichen Spiritualität geprägt zu sein.\n Elben glauben, daß sie in den Fußstapfen ihrer Vorfahren und der heiligen Urahnen wandeln. Zudem sind sie davon überzeugt, daß",0,30,1);
            base.books.AddGermanBookText("eine Seele nach dem Tod des Körpers in eine „unstoffliche“ Welt der Götter eingeht, dort lediglich eine lange Ruhepause einlegt, um über das Leben nachzudenken, sich auszuruhen und um gemeinsam mit",0,30,1);
            base.books.AddGermanBookText("anderen Seelen daraus zu lernen, nur um eine sehr lange Zeit später wieder in der Welt reinkarniert zu werden. Wann eine solche Reinkarnation geschieht ist völlig unklar, es ist die Rede von vielen",0,30,1);
            base.books.AddGermanBookText("Jahrhunderten oder gar Jahrtausenden (Elben verwenden gern den Begriff „Sieben Lebensspannen“ oder „Drei Zeitalter“) . Da jede Seele in einer vorherbestimmten „Konstellation“ wiedergeboren werden",0,30,1);
            base.books.AddGermanBookText("muß, sind zum Beispiel Verwandte, Freunde und das gesamte persönliche Umfeld einer Seele sowie die Stellungen der Sterne bei der nächsten Wiedergeburt in einer sehr ähnlichen Konstellation zu finden.",0,30,1);
            base.books.AddGermanBookText("Dabei sei es das Ziel jeder Seele, ständig zu wachsen und zu lernen, um schließlich allumfassende Harmonie im „ewigen Gleichgewicht des Seins“ zu erlangen. Für Elben hat jede Begegnung und jedes",0,30,1);
            base.books.AddGermanBookText("Ereignis in ihrem Leben einen direkten Zusammenhang mit der spirituellen Welt und der Welt der Natur- und Elementargeister, welche sie „die uralten Geister“ nennen. Für sie hat jeder Busch, jeder",0,30,1);
            base.books.AddGermanBookText("Stein und jedes Ding eine eigene Seele, das Leben und selbst die Existenz wird als heilig angesehen.\n In menschlichen Worten ausgedrückt könnte man sagen : „Eine doch sehr weltentrückte Auffassung",0,30,1);
            base.books.AddGermanBookText("der Dinge“. Zwerge, die solche Schriften in die Finger bekommen, erwähnen oft Kopfschmerzen, werfen die Schriften kopfschüttelnd davon und gehen Bier trinken, während Orks beim Ertragen solcher Worte",0,30,1);
            base.books.AddGermanBookText("gerne mit einem langgezogenen „UUUH ?!?“ reagieren oder es einfach vorziehen, den Speichelfaden abzuwischen um dann gepflegt das Bewußtsein zu verlieren.\n Man hörte jedoch von Echsenmenschen und",0,30,1);
            base.books.AddGermanBookText("erstaunlicherweise einigen Halblingen und sehr wenigen Menschen – zumeist Magiern – die es tatsächlich schafften, sich ausgiebig genug in die Mythen und Philosophien der Elben einzuarbeiten, um als",0,30,1);
            base.books.AddGermanBookText("Experten auf diesem Gebiet zu gelten. Unverwunderlicherweise gelten diese Experten oft als ein wenig verschroben und wunderlich, aber generell als extrem friedfertig und geduldig.",0,30,1);
            base.books.AddGermanBookText("~Orks~\nVerehrte Götter\n Brágon\n Malachín\n Zhambra\n Moshran\n Cherga\n Die Orkische Religion – wenn man es eine feste Struktur gibt es nicht – sieht das Leben in einem Kreislauf : Geburt,",0,30,1);
            base.books.AddGermanBookText("Jagd, Kampf, Tod.\n Der Frühling ist die Geburt des Jahres, der Sommer ist die Zeit der großen Jagden und der Kriege. Orkstämme halten in Friedenszeiten untereinander im Sommer Kriegsspiele ab, viele",0,30,1);
            base.books.AddGermanBookText("eher rauhe Sportarten stammen aus orkischen Traditionen. Auf diesen Spielen treffen sich regulär die Stämme, und weibliche Orks suchen sich ihre Männer.\n Der Herbst ist die Zeit des Sammelns und auch",0,30,1);
            base.books.AddGermanBookText("der Jagd, und im Winter herrscht die Kälte der „eisigen Hexe“, wie Cheerga oft auch von Orks genannt wird.\n Auch die Welt Illarion selbst befindet sich in diesem Kreislauf. Einst wurde sie von den",0,30,1);
            base.books.AddGermanBookText("Göttern erschaffen, weil diese Langeweile hatten, und dann wurden die Völker erschaffen und wie Würfel daraufgeworfen, um festzustellen, ob sie würdig seien zu existieren. Manche Würfel und",0,30,1);
            base.books.AddGermanBookText("Wurfknochen gelten einigen Stämmen oft als Heilige Reliquien. Orks halten ihre Ahnen in Ehren. Weise Berater oder Anführer und mächtige Krieger werden nach ihrem Tod in „Ahnenhöhlen“ beerdigt. Der",0,30,1);
            base.books.AddGermanBookText("Schädel des Gestorbenen wird in einer Felsnische aufbewahrt, direkt über seinem Körper, welcher oft in einer darunterliegenden Nische liegt, aus seinen Fingerknochen werden Wurfknochen gefertigt,",0,30,1);
            base.books.AddGermanBookText("welche oft neben dem Schädel platziert werden, damit man den Toten befragen kann. Auf diese Weise können orkische Geisterbeschwörer die Ahnen des Stammes um Rat fragen und sie ehren.\n Malachín ist",0,30,1);
            base.books.AddGermanBookText("für Orks der Gott der Jagd und des Kampfes. Brágon ist der Sonnenbringer, Lebensspender und Vernichter alles Unreinen und Schwachen. Zhambra ist der „Bruder-Gott“, er wird angerufen um Streitigkeiten",0,30,1);
            base.books.AddGermanBookText("schlichten oder um Bluts-Eide (z.B. Blutsbruderschaft oder sehr bindende, heilige Eide) zu vollziehen. Cherga ist die Totenwächterin, welche die Gestorbenen in das Land der Ahnen bringt, und",0,30,1);
            base.books.AddGermanBookText("Moshran ist der Schlachtenwüter, der im Kampf einen unbändigen Kampfrausch über die Krieger bringen kann, wenn ihm zuvor weise geopfert wurde. Er gilt allerdings als sehr wankelmütig und leicht zu",0,30,1);
            base.books.AddGermanBookText("erzürnen, weshalb viele Orks es vorziehen, vor einem Kampf zu Malachín zu beten, da es im Kampfrausch Moshrans oft unmöglich ist, Freund und Feind zu unterscheiden.\n Für Orks zählt, in spirituellem",0,30,1);
            base.books.AddGermanBookText("Sinne, die Stärke der Seele viel, welche sich in einem starken Körper und Geist wiederspiegelt.\n In der Orkischen Mythologie bringt Cherga die Gestorbenen durch das Nichts, die ewige Kluft zwischen",0,30,1);
            base.books.AddGermanBookText("den Welten, in das Land der Ahnen. Wenn ein Ork viele Gegner im Kampf besiegt hat und in seinem Leben stark war, ist sein Ansehen dort groß, und seine Ahnen werden ihn ehren. Er bekommt viele Diener",0,30,1);
            base.books.AddGermanBookText("und Sklaven, und die Frauen werden ihn lieben. War er schwach und feige, so wird er dort ein Dasein als Diener fristen müssen, wohlmöglich gar als Diener eines Menschen, Halblings oder Zwergen.",0,30,1);
            base.books.AddGermanBookText("~Flammen-Orks~\nVerehrte Götter\n Brágon\n Malachín\n Zhambra\n \n Die Orks der Flamme gehen zurück auf die Zeit der Ork-Züge, als viele Orks in elbische und teilweise auch menschliche Gefangenschaft",0,30,1);
            base.books.AddGermanBookText("gerieten und vieles von diesen lernten. So unsinnig es ist, haben diese Generationen ein Gemisch aus den Traditionell orkischen, menschlichen und elbischen Religionen zu Stande gebracht, allerdings",0,30,1);
            base.books.AddGermanBookText("verehren sie nur drei Götter, von denen sie Erlösung erhoffen.\n Flammen-Orks verstehen sich als eine Art Ork-Paladine, welche in den heißen, teilweise vulkanischen Gebieten der Norbuk-Hochebene in",0,30,1);
            base.books.AddGermanBookText("steinernen Festungen leben. Ihre Siedlungen bestehen aus quadratischen Steinhäusern, die oftmals aus dem Basalt- und Granitgestein der Berge herausgeschlagen wurden. Sie leben in beinahe militärischer",0,30,1);
            base.books.AddGermanBookText("Organisation und kämpfen manchmal fast fanatisch gegen alles „Unreine“, womit jede dämonische Kreatur oder bösartige Anhänger Moshrans gemeint sind. In ihrem Glauben vereinen sich menschliche,",0,30,1);
            base.books.AddGermanBookText("elbische und orkische Traditionen: So glauben sie, daß jedes Wesen so oft wiedergeboren wird, bis es vollkommen ist. Daher gilt es, im Leben Vollkommenheit zu erreichen: Vollkommenheit des Körpers,",0,30,1);
            base.books.AddGermanBookText("des Geistes und der Gesellschaft. Aus diesem Grund verlassen Flammen-Orks sehr selten ihre Festungen, da sie alle anderen Gesellschaftsformen (erstaunlicherweise bis auf traditionelle",0,30,1);
            base.books.AddGermanBookText("Elbensiedlungen) als unvollkommen und daher schädlich für Körper und Geist erachten. Sie trainieren von Kind auf gleichwohl ihren Körper im Kampf wie auch ihren Geist durch Rätsel, Religion,",0,30,1);
            base.books.AddGermanBookText("Dichtkunst und Philosophie.\n Sie glauben wie die Elben, daß die fünf alten Götter die Welt einst erschufen, und daß die elf Jüngeren Götter einst Sterbliche gewesen seien, welche die absolute",0,30,1);
            base.books.AddGermanBookText("Vollkommenheit erreicht haben.\n Sie treiben regelmäßig Handel mit anderen Völkern und bewachen die Zugänge der Hochebene. Sie verbünden sich mit ihren „wilden“ Brüdern, den orkischen Clans, wenn",0,30,1);
            base.books.AddGermanBookText("aufgrund von Ungerechtigkeiten Streitigkeiten zwischen den Clans ausbrechen, und arbeiten sogar manchmal als Diplomaten, welche die Interessen der Stämme und ihrer Festungen bei anderen Völkern",0,30,1);
            base.books.AddGermanBookText("vertreten.\n Ihre religiösen Auffassungen gelten generell bei den Orkstämmen als leicht „bescheuert“, jedoch wird jedem einzelnen dieser Mönche und Paladine von den Stämmen enormer Respekt",0,30,1);
            base.books.AddGermanBookText("entgegengebracht, in deren Augen sie die Erwählten Kinder Brágons sind.\n Ein Flammen-Ork wird jede Gelegenheit nutzen, die ihm die Götter bieten, um auf die Welt zurückzukehren, sollte er einmal den",0,30,1);
            base.books.AddGermanBookText("vorzeitigen Tod erleiden – daher errichten die Flammen-Orks viele Stelen und Altäre in den Ländereien, während sie zugleich versuchen, ihren „wilden“ Brüdern Zivilisation zu geben und sie teilweise",0,30,1);
            base.books.AddGermanBookText("auch zu missionieren. Manchmal werden Ausgestoßene Orks, welche in der Wildnis lang genug überleben, nach langen Prüfungen, Belehrungen und Indoktrinierungen in ihre Reihen aufgenommen – diese haben",0,30,1);
            base.books.AddGermanBookText("oft den ständigen Drang, sich in den Augen der Götter und ihrer neuen Brüder zu beweisen, und ziehen oft sogar durch die Welt, um Heldentaten zu vollbringen.\n Flammen-Orks glauben, daß sie nach dem",0,30,1);
            base.books.AddGermanBookText("Tode vor das „Tribunal der Ewigkeit“ treten. Brágon wird feststellen, ob sie gerecht und im Sinne der heiligen Flamme gelebt haben, Malachín wird feststellen, ob sie ehrenhaft und diszipliniert",0,30,1);
            base.books.AddGermanBookText("gekämpft haben, und Zhambra wird feststellen, ob sie loyal zu ihren Freunden und gehorsam ihren Anführern gegenüber waren. Dann beraten die drei, und urteilen über ihn. Wurde er den Anforderungen",0,30,1);
            base.books.AddGermanBookText("nicht gerecht, so muß er in die Welt Illarion zurückkehren, um dort erneut sein Glück zu versuchen, seine Zeit ist dann noch nicht gekommen, weil er noch Aufgaben zu erfüllen hat. Sollte er mit voller",0,30,1);
            base.books.AddGermanBookText("Absicht versagt und aus Niedertracht gehandelt haben, so wird er bestraft oder in der ewigen Flamme eingeschmolzen, um erneut im Kreislauf der Natur verwendet zu werden. Wurde er jedoch allen",0,30,1);
            base.books.AddGermanBookText("Anforderungen gerecht, so darf er ins Jenseits eintreten, um dort zu rasten und mit alten Freunde zu trinken, bevor sie sich nach ausgiebigen Jahrhunderten des Feierns und Zechens gemeinsam wieder",0,30,1);
            base.books.AddGermanBookText("auf die Welt begeben, um weiter nach der Vollkommenheit zu streben.\n \n~Blutorks~\nReligion siehe: Orks\n \n Die oftmals kaum intelligenten Blutorks fristen ihr Dasein nur als wütende,",0,30,1);
            base.books.AddGermanBookText("blutdürstige Kampfmaschinen, deren einzige Aufgabe darin besteht, so viel Blut wie möglich zu vergießen, wenn ihre Kameraden sie von der Kette lassen – allerdings ist es ihnen völlig egal ob es sich",0,30,1);
            base.books.AddGermanBookText("dabei um das Blut von freundlichen oder feindlichen Personen handelt. In Schlachten dreht man sie in eine bestimmte Richtung und sagt: „Töte DA.“ Mehr verstehen sie auch kaum.\n Blutorks sind Orks,",0,30,1);
            base.books.AddGermanBookText("die in einem Kampfrausch „hängengeblieben“ sind – die Meisten sehen dies als Fluch an, manche jedoch als Segen. Ihre Lebensspanne ist zumeist sehr kurz, da sie sich bei nahezu jeder Gelegenheit",0,30,1);
            base.books.AddGermanBookText("wild in den Kampf stürzen und von ihren Brüdern immer direkt in die erste Schlachtreihe gestellt werden.\n Blutorks leben in einem nahezu ständigen Blutrausch und sind kaum in der Lage, einen klaren",0,30,1);
            base.books.AddGermanBookText("Gedanken zu fassen. Sie geben sich völlig ihren Instinkten hin oder werden von diesen beherrscht.\n Manche von ihnen, die selbst für ihren Stamm zu wild wurden, leben ständig angekettet oder fast",0,30,1);
            base.books.AddGermanBookText("wie Raubtiere in der Wildnis.\n Die „Philosophie“ eines Blutorks – in sehr ruhigen Zeiten, wenn dieser in der Lage ist überhaupt einen klaren Gedanken zu fassen – ist entweder die, möglichst viele",0,30,1);
            base.books.AddGermanBookText("Gegner zu töten oder der Versuch, wieder „normal“ zu werden.\n Danach richtet sich auch ihre „Religion“: Entweder, sie beten zu Cherga, daß sie ihr überhitztes Gemüt abkühlt und zu Zhambra, daß er sie",0,30,1);
            base.books.AddGermanBookText("wenigstens ihre Freunde erkennen läßt, Oder sie beten zu Moshran, daß er sie in einem Glorreichen Kampf sterben läßt und ihren Blutrausch so hoch treibt, daß sie möglichst viele Gegner erschlagen.",0,30,1);
            base.books.AddGermanBookText("Ein ehemaliger orkischer Feldherr, der in einem Blutrausch „hängenblieb“ und so zu einem Blutork wurde, soll einmal in einem ruhigen Moment gesagt haben : „Ich denke nicht, denn ich bin bereits Tot.",0,30,1);
            base.books.AddGermanBookText("Von meiner Axt trieft das Blut der Erschlagenen, und in meinen Adern rinnt brennend der reine Zorn der Götter. Ich bete um Erlösung von diesem Schmerz und von dieser.. STARR MICH NICHT SO AN –",0,30,1);
            base.books.AddGermanBookText("RAAAAARGH !“\n Blutorks, die diesen Rausch „überlebten“ und ihre Instinkte wieder unter Kontrolle brachten, schafften dies meist unter Anleitung und Hilfe eines Schamanen, Hexenmeisters oder Heilers.",0,30,1);
            base.books.AddGermanBookText("~Echsenmenschen~\nVerehrte Götter\n Zelphia\n \n Die Religion der Echsenmenschen, welche durchaus auch andere Götter kennt, verehrt ausschließlich nur eine Gottheit anora, die Göttin des Wassers. Ihr",0,30,1);
            base.books.AddGermanBookText("Ihr Name in der Sprache der Echsen lautet allerdings „Zelphia“. In ihrer Mythologie stammen die Echsenmenschen direkt von Tanora ab. Ihre Schöpfungsgeschichte ähnelt stark der der Elben. Die anderen",0,30,1);
            base.books.AddGermanBookText("Götter sind jedoch Götter der anderen Völker und daher für einen Echsenmenschen kaum von Bedeutung. Auch wenn er sie nicht erzürnen will und ihre Anhänger respektiert, wird er nicht zu ihnen beten.",0,30,1);
            base.books.AddGermanBookText("Echsen glauben, daß sie nach ihrem Tod ein Meer durchschwimmen oder durchtauchen müssen. Haben sie ehrenhaft, demütig und im Sinne der Göttin gelebt, dann so ist das Meer ruhig. Sie finden unterwegs",0,30,1);
            base.books.AddGermanBookText("genug schmackhafte Fische, und sie erreichen die heiligen Inseln der Ewigkeit unbeschadet und nach einer angenehmen Reise.\n Waren sie jedoch unehrenhaft, hochmütig oder selbstsüchtig, dann wird",0,30,1);
            base.books.AddGermanBookText("ihnen für jede Untat ein Stein umgebunden, den sie mit auf die Insel bringen müssen. Jeder Stein zieht sie auf den Grund und verlangsamt und erschwert damit ihr Vorankommen, denn auf dem Grund gibt",0,30,1);
            base.books.AddGermanBookText("es Täer, Felsen und Berge, die erstiegen oder umgangen werden müssen. Auch mag es passieren, daß das Meer ihnen unterwegs „Streiche“ spielt, in der Form von lästigen Tintenfischen und Feuerquallen",0,30,1);
            base.books.AddGermanBookText("zum Beispiel. Solche Echsen, die nur kleine oder gar keine Steine mit sich nehmen müssen, können einem Steinträger jedoch Gnade erweisen und ihm einen einzelnen Stein abnehmen.\n Echsen glauben nicht",0,30,1);
            base.books.AddGermanBookText("wie die Elben an Wiedergeburt. Für sie sind die Altäre und Stelen der Götter sowie heilige Rituale die einzigen Möglichkeiten, aus der Jenseitigen Welt oder von der Inseln der Ewigkeit nach Illarion",0,30,1);
            base.books.AddGermanBookText("zurückzukehren.\n Jedoch gewährt Zelphia den Zurückkehrenden einen Gefallen : Damit sie sich nicht im Leben nach der Schönheit der ewigen Inseln vor Sehnsucht verzehren, legt die Göttin ein Band über",0,30,1);
            base.books.AddGermanBookText("alle Erinnerungen an das Land der Toten.\n \n~Hablinge\nVerehrte Götter\n Oldra\n Adron\n Elara\n Ronagan\n Sirani\n Zhambra\n Nargùn \n \n Die Mythen der Halblinge sind bemerkenswert.\n Es heißt",0,30,1);
            base.books.AddGermanBookText("in ihrer Schöpfungsgeschichte: „Am Anfang erschufen die Schöpfer die Welt aus einem Apfel. Dann erschufen sie die Götter aus Kirschkernen. Nach all dieser Arbeit zogen sie sich von Illarion zurück,",0,30,1);
            base.books.AddGermanBookText("um sich zu erholen und ihr Werk mit Speis und Trank zu feiern.\n Den Göttern jedoch war das Bestellen aller Felder viel zu viel Arbeit, darum nahmen sie wiederum Sandkörner und erschufen die Völker",0,30,1);
            base.books.AddGermanBookText("der Menschen und Halblinge. Damit auch jemand die Früchte des Wassers ernten möge, erschufen sie die Echsen, und damit auch die Waldfrüchte nicht ungepflückt blieben, erschufen sie die Elben. Die",0,30,1);
            base.books.AddGermanBookText("Zwerge wurden erschaffen, um die Früchte des Berges gegen essbare Früchte einzutauschen. \n Die Orks jedoch wurden erschaffen, nachdem sich der Ungenannte, den man auch den Milchversaurer nennt, den",0,30,1);
            base.books.AddGermanBookText("Magen an einem alten Kuchen verdarb, den er Nargùn gestohlen hatte, welcher damit eigentlich einen Riesen hatte erschlagen wollen...“\n Halblinge glauben, daß jemand, der gestorben ist, in das",0,30,1);
            base.books.AddGermanBookText("Paradies eingelassen wird, wenn er gut, gerecht und zufrieden gelebt hat. Dort wachsen Kuchen und Braten auf Bäumen und Büschen, Bier, gesüßte Milch, herrlicher Wein verschiedenster Sorten und auch",0,30,1);
            base.books.AddGermanBookText("klares Wasser fließen aus verschiedenen Quellen, und es gibt keine Fliegen und Ameisen, die in den Picknickkorb krabbeln.\n Dort werden ständig Feste gefeiert, und es gibt kaum etwas, worüber man",0,30,1);
            base.books.AddGermanBookText("sich ärgern muß, da jeder überallhin gehen kann wo es ihm beliebt. Böse Wesen jedoch gelangen nicht in das Paradies. Ihnen wird der Einlaß solange verweigert, bis sie sich ehrlich und aufrichtig",0,30,1);
            base.books.AddGermanBookText("gebessert haben, und sie dürfen erst dann dort leben wenn sie sich bei Jedem aufrichtig entschuldigt haben, dem sie zu Lebzeiten Übles antaten.",0,30,1);

            base.books.AddEnglishBookText("\n \n Religions of Illarion",0,0,1);
            base.books.AddEnglishBookText("~Norodaj Creeds~\nWorshipped Deities\n Tanora\n Irmorom\n Malachin\n Zhambra\n \n The Norodaj mainly worship Tanora as Goddess of water and seafaring, as well as as Malachin, the heroic hunter and",0,30,1);
            base.books.AddEnglishBookText("warrior.\n In the creation mythology of the Norodaj it is said that Tanora had born the earth from the ocean, and Irmorom had hewn mountains from the earth. The stones he carved off fell into the",0,30,1);
            base.books.AddEnglishBookText("The humans were then washed up onto land. Malachin had settled there taught them  hunting, but they used the knowledge of the spear to do battle amongst eachother and ate one another. When Malachin  saw",0,30,1);
            base.books.AddEnglishBookText("this he asked Tanora for advice: \"The humans war among eachother. They slay eachother.  What can we do so that this may end?\" Tanora meditated over the question and gave Malachin a boat. She sent him",0,30,1);
            base.books.AddEnglishBookText("to a distant isle where he would find advice. When Malachin reached the island, he  perceived his brother Zhambra, who was wrestling with a giant. Otherwise there was nothing else on the island but  a",0,30,1);
            base.books.AddEnglishBookText("single, wilted flower, from which fruits were hanging.\n And so, he called out to the  fighters: \"Lo there brother, Lo there giant; what do ye do there? Halt immediately, and cease to bash thine  heads",0,30,1);
            base.books.AddEnglishBookText("in like the humans!\"\n Both giant as well as Zhambra were ashamed to have been compared  with the humans and turned a bright red. Then Zhambra spoke: \"Long ago I planted the seed of the flower hither;",0,30,1);
            base.books.AddEnglishBookText("its two blossoms carry the names of Friendship and Honor, and three years ago I  returned to harvest them. But lo there I found the giant, and he allows not that I finish the task. We have wrestled  now",0,30,1);
            base.books.AddEnglishBookText("for three years already, and I grow tired of it.\" The giant spoke: \"Long ago I came  hither to this island, and discovered the wonderful flower whose blossoms are named Friendship and Honor. I  guarded",0,30,1);
            base.books.AddEnglishBookText("and nursed this flower, and now Zhambra wishes to break its blossoms! We have wrestled  for three years already, and I grow tired of it.\"\n Then it happened that Irmorom laughed, and he pointed at  the",0,30,1);
            base.books.AddEnglishBookText("its blossoms had transformed into fruits. \"Lo there, behold, the blossoms are no more.  Withered has the flower, but fruits it has born, fruits that carry seeds within them!\"\n With this,",0,30,1);
            base.books.AddEnglishBookText("Zhambra took the fruits, gave the giant two of the seeds, thanked him, and ventured to  the humans in Malachin's company. Them too he handed the blossoms of Friendship and Honor, and they ceased to,",0,30,1);
            base.books.AddEnglishBookText("slay eachother. They carried the seeds in their hearts all the way into the halls of  the dwarvenkind, who are the children of Irmorom, who is a the brother of Malachin, and also to the temples of  the",0,30,1);
            base.books.AddEnglishBookText("lizardfolk.\n According to the faith of the Norodaj, humans traverse the ocean in a  boat after death, in the search of the Island of Eternity, Ilhagar, upon which the Gods dwell. Those who were",0,30,1);
            base.books.AddEnglishBookText("righteous and honorful will find the island quickly, and Tanora will provide them with  calm waters and a steady breeze. Those however, who were of cowardice, treachery, and greed, might face stormy",0,30,1);
            base.books.AddEnglishBookText("seas and find only deserted wastelands of islands, inhabited by monsters and giants, or  even be devoured by a marinal behemoth. If these souls even happen to sail back in return to Illarion, they",0,30,1);
            base.books.AddEnglishBookText("might haunt the mortals as restless spirits, until a holy man frees them from their  fate. He would send them with sacred sorcery back to the Island of Eternity.\n All those who were not righteous  and",0,30,1);
            base.books.AddEnglishBookText("honorful will finally reach Ilhagar after a long and arduous odyssey, in order to  either serve the honorful into all eternity, or even regain their honor themselves.",0,30,1);
            base.books.AddEnglishBookText("~Serinjah Creeds~\nWorshipped Deities\n Malachin\n Cherga\n Sirani\n Ronagan\n Nargùn\n Elara\n Adron\n \n These folks of the plains mainly worships Malachin, the God of battle and hunting, as well",0,30,1);
            base.books.AddEnglishBookText("as most of the younger Gods. Most of the elder Gods are unknown to them, and they also  dub them \"The Gods of the Elves and Wizards\". They do not worship Moshran and his name is generally avoided",0,30,1);
            base.books.AddEnglishBookText("to speak it is sacrilege to them and summons great calamity—as such, his name is only  known to the sages and scribes of this tribe.\n The creation mythology of the Serinjah accounts that the world  was",0,30,1);
            base.books.AddEnglishBookText("formed from \"ancient powers\", and that these ancient powers also gave birth to the Gods  of all races at the very same time. Those deities known to the Serinjah guard and watch over the balance",0,30,1);
            base.books.AddEnglishBookText("between the ancient powers; they are teachers of mankind. Malachin had taught them how  to fight and how to hunt. Ronagan showed them how to sneak up onto the Manku-moose, Kosai-Plains buffalo, and  the",0,30,1);
            base.books.AddEnglishBookText("Onashai-birds (a large ostrich-like bird, which can be used as a mount), in order capture them alive, and how to move unseen between one's enemies. Sirani taught them the high arts of poetry and",0,30,1);
            base.books.AddEnglishBookText("romance, Elara taught them of ancestral honor and knowledge. Adron taught them how to  win wine from the corns of the plain's grass and the weed's berries. Nargûn showed the humans their own  stupidity",0,30,1);
            base.books.AddEnglishBookText("and taught them to learn from it and laugh about it, and Cherga the death warden taught  them to honor the ancestors and commit sacrifices for them.\n In the creed of the plainsfolk, the deceased",0,30,1);
            base.books.AddEnglishBookText("travel to the far realms of Ur, the Land of Eternal Summer. Everything there is in  abundance, and not one person needs steal or fear for one's life, for one can neither die nor be injured there. Thus,",0,30,1);
            base.books.AddEnglishBookText("~Ama-Shoon Creeds~\nWorshipped Deities\n Findari\n Sirani\n Cherga\n Elara\n \n The  Ama-Shoon are an amazon tribe of female Serinjah. Their mythology is akin to the plainsfolk, with the difference",0,30,1);
            base.books.AddEnglishBookText("that they pray exclusively to the females of the known deities. Additionally, they  worship Findari, who is known to the Serinjah merely as a \"Goddess of the Elvenkind\". The Ama-Shoon somewhat see",0,30,1);
            base.books.AddEnglishBookText("themselves as an honorary guard of wind itself or Findari. They protect the lands and the surrounding forests. According to their perception of things, men were touched by Nargùn, who confused the",0,30,1);
            base.books.AddEnglishBookText("spirit of all male kin, and as such, they cannot serve the Goddess.\n When an amazon warrior of the Ama-Shoon dies—as their creed has it—she is trialed by Findari. Should she fail, or had she failed",0,30,1);
            base.books.AddEnglishBookText("in life, she must give up her spear and go to dwell with the other Serinjah.\n \n~Salkmaraerians Creeds~\nWorshipped Deities\n Zhambra\n Oldra\n Elara\n Sirani\n Malachin\n Adron\n Cherga\n Irmorom",0,30,1);
            base.books.AddEnglishBookText(" Ronagan\n Nargùn\n \n The Salkamaerians worship all the young Gods, foremost Zhambra, Oldra, Elara, Sirani, and Malachin—except Moshran, whose name may not be uttered, lest it unleash great disaster",0,30,1);
            base.books.AddEnglishBookText("Every noble house of the Salkamaerians traces a family tree of their own back to the younger Gods. The elder Gods are mostly unknown to them or are simply called the \"Elven and Lizardfolk's Gods\";",0,30,1);
            base.books.AddEnglishBookText("other religions are politely tolerated.\n The creation mythology of the Salkamaerians says that the holy Gods themselves lived in the ancient city Salkamar, which hovered in the midst of the Nothing,\"; ",0,30,1);
            base.books.AddEnglishBookText("and in those days, still bore a name in the tongue of the Gods. Surrounding the Nothing, elementary Chaos raged on. As such, Nargûn, Lord of Chaos, rose and scolded the Chaos and the Nothing, for\";",0,30,1);
            base.books.AddEnglishBookText("neither of them appealed to him. He persuaded the other Gods to create a whole world. And so the Gods created the world. The world appealed to them though, and so they left the city and built more, ",0,30,1);
            base.books.AddEnglishBookText("neither of them appealed to him. He persuaded the other Gods to create a whole world. And so the Gods created the world. The world appealed to them though, and so they left the city and built more,",0,30,1);
            base.books.AddEnglishBookText("smaller worlds—every God had one of their own, and more.\n However, Cherga saw that should the worlds collide, all would transform back into Chaos, and so she spoke: \"Let us fill the space between the",0,30,1);
            base.books.AddEnglishBookText("worlds with the Nothing, so that Illarion may be divided from the other worlds, and the  smaller worlds from eachother.\" And Sirani called: \"And let us transfix the worlds around Illarion, so that one",0,30,1);
            base.books.AddEnglishBookText("can admire their beauty from thither!\"\n So it came to be. The worlds themselves are the stars which one can see from Illarion. They are cubes like Illarion floating in the Nothing, only smaller;",0,30,1);
            base.books.AddEnglishBookText("centered and ordered around their center, Illarion. Hailing from these other worlds, the Gods later sent forth new tribes and races. And because the Gods had left the world, and only the clergy could",0,30,1);
            base.books.AddEnglishBookText("harness their power in their names, the race of humans became mortal.\n Thus, upon death, Cherga the Death Guard delivers the deceased into the realm of the God whom meant the most to the human, and",0,30,1);
            base.books.AddEnglishBookText("who fits the most to that human's soul—usually, this is the house patron deity of one's  own family, or of the wife or husband. If the dead was honorable and righteous, that human will attain a",0,30,1);
            base.books.AddEnglishBookText("pleasant afterlife, but else if the human was selfish and deceitful, he will not reach  the world of his God, and instead will be condemned to wander through Cherga's gray Nothing for all eternity",0,30,1);
            base.books.AddEnglishBookText("until his mistakes are forgiven or until Cherga grabs him and hurls him back to Illarion. Should he hit the grounds of the world, the impact will shake ALL his memories from himself, and his spirit",0,30,1);
            base.books.AddEnglishBookText("will be completely emptily born into the body of an infant. Should he fall softly, into  waters or a roof of leaves, then it may occur that he becomes a restless spirit.\n Holy men and priests of",0,30,1);
            base.books.AddEnglishBookText("Cherga have the mission to release this spirits, so Cherga may throw them again. The dead oftenly walk the \"Path of the Worlds\" in the heavens and visit eachother in the realms of each other's Gods.",0,30,1);
            base.books.AddEnglishBookText("Every time a carriage traverses from one world to another, the humans behold a shooting  star.\n Sometimes the ancestors wish to observe their children or send them important messages in their dreams.",0,30,1);
            base.books.AddEnglishBookText("Then a heavy stone is attached to the heavenly chariots, so they are heavy enough to descend upon Illarion. Once the message or dream is delivered, the carriage and the spirit disappear to from whence.",0,30,1);
            base.books.AddEnglishBookText("they came, and only the stone remains.\n \n~Albarian Creeds~\nWorshipped Deities\n Adron\n Malachin\n Irmorom\n Zhambra\n Nargùn\n Ronagan\n Moshran\n \n Presumably due to their usual patriarchal",0,30,1);
            base.books.AddEnglishBookText("forms of lordship, only male Gods are worshipped in the Albarian realm, although all deities also worshipped in Salkamar are known to them as well. Any temple of a female Goddess is forbidden in",0,30,1);
            base.books.AddEnglishBookText("the Albarian realm—to worship them counts as weakly and as an incitement of the people.  Travelers are oftenly bid \"politely\" and persistently to keep their beliefs to themselves. Whoever opposes this",0,30,1);
            base.books.AddEnglishBookText("in any way, will most oftenly face a long visit in a dungeon, public humiliation and lashings, and eventually be escorted to the borders and \"cast away\" with threats of even worse punishments. Every",0,30,1);
            base.books.AddEnglishBookText("year in Albar, there are two or three \"Days of Lashing\" in all the cities, during which  several foreigners—who were too stubbornly representing their creeds towards female deities—face cruel",0,30,1);
            base.books.AddEnglishBookText("treatments: For public amusement, the Albarians cast a plethora of animal dung, rotten  fruit and eggs at these prisoners, and finally they are dealt thirty whip lashings. Male prisoners even have to",0,30,1);
            base.books.AddEnglishBookText("walk their way to the borders vesting women's garments.\n All in all, the Albarian mythology is the same as the Salkamaerian, save the difference that the female deities are mentioned in the",0,30,1);
            base.books.AddEnglishBookText("mythology, but it is forbidden to worship them.\n \n~Gynkeesh~\nWorshipped Deities\n Elara\n Adron\n Oldra\n Cherga\n Malachin\n Irmorom\n Sirani\n Zhambra\n Ronagan\n Nargùn\n Moshran",0,30,1);
            base.books.AddEnglishBookText("In Gynk, a city whose houses are built on hills and poles over a swamp, two cultures clashed together: Many Albarian refugees (former peasants and commoners) and some Salkamaerian refugees (criminals",0,30,1);
            base.books.AddEnglishBookText("and other escapees) founded a culture and society of their own, many centuries ago. All  of the young Gods are worshipped, and there is a temple for every deity in Gynk. Because there are also",0,30,1);
            base.books.AddEnglishBookText("lizardfolk and elvenkind dwelling in the city, there is even a temple of the Five and a  water temple in Gynk—the human populace is completely indifferent about it.\n The mythology of Gynk corresponds",0,30,1);
            base.books.AddEnglishBookText("to those of Salkamar and Albar, but there are some Gynkeesh who have assumed the creed  of another race.\n \n~Black Cultists\nWorshipped Deities\n Moshran\n Bjolmur\n Dragorog\n Cherass",0,30,1);
            base.books.AddEnglishBookText("There are Black Cultists from nearly any tribe or race (although it is as likely as never for halflings or elvenkind to become Black Cultists). The Black Cultists are infamous throughout the world",0,30,1);
            base.books.AddEnglishBookText("and they are followers or even clerics of Moshran or one of his \"children\". They have faith only in that the utmost strongest and devious will triumph over all others in the end. They refrain from",0,30,1);
            base.books.AddEnglishBookText("doing anything which they expect no personal benefit or gain from. And they never do any favors, because such could be interpreted as \"goodness\" or \"weakness\".\n Oftenly, they employ the magickal",0,30,1);
            base.books.AddEnglishBookText("schools of Necromancy and Demonology. This creed is however forbidden in all realms, because they always use this knowledge recklessly and exclusively for their own selfish benefits. This is the",0,30,1);
            base.books.AddEnglishBookText("reason why they oftenly operate from the dark, under a shroud of secrecy. These cults usually only exist a very few years, because the members of these group tend to murder eachother and denounce",0,30,1);
            base.books.AddEnglishBookText("eachother publically, in order to occupy the position of those who they \"moved out of the way\". At latest when the public or authorities has learned of their doings and proximity, their doings",0,30,1);
            base.books.AddEnglishBookText("and lives tend to find an end.\n Creation mythology is completely meaningless to the Black Cultists. Only the stronger will be victorious in the end, and that is no doubt to them Moshran or one of",0,30,1);
            base.books.AddEnglishBookText("his three immortals. Against this, the past holds no weight. They are convinced that  after death they will find rank and file in the armies of Moshran. Whoever in life was cunning, wily, and",0,30,1);
            base.books.AddEnglishBookText("useless conflict, or end up as a servatile spirit of a drunkard goblin.\n \n~Dwarven Creed~\nWorshipped Deities\n Irmorom\n Ushara\n Cherga\n \n The national creed of the dwarvenkind reflects the",0,30,1);
            base.books.AddEnglishBookText("dwarven traditions. Although the dwarves believe in the existance of the other Gods and they are present in their mythologies, it is extremely rare that a dwarf would forget his upbringing and pray",0,30,1);
            base.books.AddEnglishBookText("to any other deity than Irmorom, Ushara, or Cherga. The other deities have nothing to do with the dwarves—with Irmorom's assistance, of course, they have created the other races. Irmorom, also titled",0,30,1);
            base.books.AddEnglishBookText("as the \"Dwarven Father\" or \"Longbeard\", is supposed to be the creator of the dwarves. He had hewn them from the stone itself, and under the eyes of the dwarves he created the bones of the other races",0,30,1);
            base.books.AddEnglishBookText("and animals, because bones are naturally made of stone—their light weight is explained only in that Irmorom was so wise to punch tiny holes into them, as so they could be lighter and more sturdy. Who",0,30,1);
            base.books.AddEnglishBookText("else other than a God would be able to perform such pieces of artwork?\n Among the dwarvenkind, there are many interpretations and philosophies of the happenstances during the era of creation. For the",0,30,1);
            base.books.AddEnglishBookText("sake of example, \"The Revelation of Tialdin\" is detailed here. It deals with a partially interpreting compilation of copies of ancient dwarven scripture, which the king had discovered in an old mine",0,30,1);
            base.books.AddEnglishBookText("of the legendary seventh mountain of the dwarven realms. The majority of dwarvenkind believes that Irmorom himself had hewn Illarion from a stone from the cliff of the mountain Borag-Urubur, the",0,30,1);
            base.books.AddEnglishBookText("\"Mountain of Origin\", and thus created the world. Ushara, by Irmorom's bidding, covered the bare rocks with earth; Tanora added some water, and Findari encompassed all with air; upon which Brágon",0,30,1);
            base.books.AddEnglishBookText("brought fire to provide Illarion with warmth and illumination. The other Gods created the plants and the creatures. When Irmorom created the dwarves and gave them bones of stone, the other Gods bid",0,30,1);
            base.books.AddEnglishBookText("him for the stone bones as well, so they too could devise sturdy creatures with them. Moshran on the other hand, crafted creatures from Chitin for them to have a shell, and filled them with mucous",0,30,1);
            base.books.AddEnglishBookText("(which he had stolen from Nargûn when the God of Chaos had suffered a cold). These creatures served the sole purpose to disgust the dwarves and other races and instill them with fear, and in some",0,30,1);
            base.books.AddEnglishBookText("cases, eat them. Irmorom however recognized some of their potential, and so it came that he blessed some of their kind, making them to nutritious delicacies.\n In consequence, Moshran was so",0,30,1);
            base.books.AddEnglishBookText("frustrated that even he bid Irmorom for bones of stone and Brágon for assistance to create the orcs—the race that, till today, holds the potential to drive the most patient dwarves into a frenzied",0,30,1);
            base.books.AddEnglishBookText("rage, and hold absolutely no potential for delicacies, rather as a target.\n It is the belief of the dwarves that in the afterlife, Cherga leads them to an infinitely huge mountain, the",0,30,1);
            base.books.AddEnglishBookText("\"Borag-Urubur\". This mountain is the home of the Gods and so large, that the world of Illarion was hewn from a single stone of its slope.\n There, the ancestors await them and accompany them to the",0,30,1);
            base.books.AddEnglishBookText("eternal halls of the mountain. Irmorom will ask them personally about their lives, and every dwarf who speaks truthfully and has lived honorably and righteously, shall be accepted by him into the",0,30,1);
            base.books.AddEnglishBookText("eternal halls. Those who lie and lived dishonorably are banished into the land outside of the gates to the halls, where they dwell as peasants and shepherds; which wouldn't be all too bad, if it was",0,30,1);
            base.books.AddEnglishBookText("not for the forests of the Longears (Elvenkind) and the plains of the Pignoses (Orcs) being immediately nearby. The more honorful, reputed, and glorious the life of a dwarf was, the more glorious and",0,30,1);
            base.books.AddEnglishBookText("honorful will his afterlife be.\n It is also a common belief among dwarves, that Irmorom sometimes already rewards a wise dwarf with fortune and loyal fellowship in their lifetimes.",0,30,1);
            base.books.AddEnglishBookText("~Elvish Creed~\nWorshipped Deities\n Eldan\n Findari\n Ushara\n Brágon\n Tanora\n \n All of the younger deities are known to the elves. They are worshipped, but generally not as deities. Elves",0,30,1);
            base.books.AddEnglishBookText("oftenly dub the younger Gods \"holy ancestors\".\n The traditional faith of the elves, most oftenly only called \"Path of the Seeker\", is complicated to comprehend by a human. This may be accounted for",0,30,1);
            base.books.AddEnglishBookText("by the fact that most of the elves only seldomly exchange the details with members of other races. Still though, some lizardfolk, humans and halflings who proved themselves to be tolerant, patient,",0,30,1);
            base.books.AddEnglishBookText("and possessing a thirst for knowledge, were handed some copies of religious-philosophical scriptures of the elvenkind. Overall, the center of the \"Path of the Seeker\" seems to be comprised of",0,30,1);
            base.books.AddEnglishBookText("tolerance, personal development of the individual, an all-encompassing balance, meditation, and principal spirituality.\n The elves believe that they wander in the tracks of their predecessors and the",0,30,1);
            base.books.AddEnglishBookText("holy ancestors. They are also convinced that their spirit enters an \"ethereal\" world of the divine after the death of their body, where the soul merely enters a state of recreation, in order to rest",0,30,1);
            base.books.AddEnglishBookText("and learn from it, and then after a long time be reincarnated in the world. When such a reincarnation is supposed to happen is completely undefined, and it is said that it could deal in centuries or",0,30,1);
            base.books.AddEnglishBookText("even millenia (elvenkind enjoy using the term \"Seven high spans\", or \"three eras\"), but what is meant for sure is defined nowhere. The reason hitherto is presumably that time seems rather unimportant",0,30,1);
            base.books.AddEnglishBookText("to elves.\n Because every soul must be reborn in a predefined \"constellation\", relatives, friends, the entire personal environment of a soul, and even the constellations of the stars are found to",0,30,1);
            base.books.AddEnglishBookText("stand in a very similar constellation upon the next reincarnation. In this process, the goal of every soul is to continuously grow and learn, in order to finally attain all-encompassing harmony in the",0,30,1);
            base.books.AddEnglishBookText("\"Eternal Balance of Being\". For elves, every encounter and every event in their lives has a direct correlation with the spiritual world and the world of the nature spirits and the elemental spirits;",0,30,1);
            base.books.AddEnglishBookText("which they call the \"ancient spirits\". For them, every bush, every stone, and every single object has a soul of its own—they regard life and even existance itself as holy.\n In human words, one could",0,30,1);
            base.books.AddEnglishBookText("say: \"A rather alienated perception of things.\" Dwarves who get hold of scriptures of the elvenkind often complain about head-aches and throw the parchments away, shaking their heads, and heading off",0,30,1);
            base.books.AddEnglishBookText("say: \"to drink beer; while orcs who are subject to these words preferably react with an elongated \"Uuuh?!\" or simply wipe the thread of saliva that hangs from their mouth and then fall unconscious in a",0,30,1);
            base.books.AddEnglishBookText("controlled fashion. However, there are tales of lizardfolk, and astonishingly enough of halfling and very select few humans—mostly magi—who actually succeeded in delving so deep into the myths and",0,30,1);
            base.books.AddEnglishBookText("philosophies of the elvenkind to be considered experts in the area. It comes as no surprise that these experts also are considered to be somewhat eccentric and wondrous, but generally extremely",0,30,1);
            base.books.AddEnglishBookText("peaceful and patient.\n The other races deem the elven creation mythology to be highly abstract, because it contains many odd facets. There are also many different forms of retelling the elven creation",0,30,1);
            base.books.AddEnglishBookText("mythology. They all only differ in the individual style of the recollection, the meaning and the history however are always identical. Many of these texts are abstract and complex song stanzas or",0,30,1);
            base.books.AddEnglishBookText("poems—a pure form of art in honor of the creators—only comprehensible to the listener or reader when he or she already knows the history from one of the rather \"normal\" variations of the texts, or",0,30,1);
            base.books.AddEnglishBookText("Ilkamaerian scholar. The Copies are several thousands of years old and ever since have been translated into various human dialects. The following excerpt shall illustrate the complexity of the elven",0,30,1);
            base.books.AddEnglishBookText("creation mythology. The Copies are a summarizing except themselves, taken from the original, elven texts:\n \"And behold, 'twas night in the world, and the emptiness held reign. 'Twas night, and the ",0,30,1);
            base.books.AddEnglishBookText("emptiness desired the light. For every being, even the emptiness, strives towards the thought. And yet was the emptiness not able to give birth to the thought, for it was the emptiness itself—but",0,30,1);
            base.books.AddEnglishBookText("in emptiness, peace and harmony held reign.\n \"And behold, the ancient substance created the source of creation, the spark of life, which once had summoned the creators to life. \"And so the emptiness",0,30,1);
            base.books.AddEnglishBookText("summoned the creators.\n \"The creators entered the emptiness and sealed the gates of arrival. For so 'twas how the light entered the emptiness, and they united, and there was only the illuminated",0,30,1);
            base.books.AddEnglishBookText("emptiness and the creators.\n \"The creators sang into the emptiness, and behold: So it came that every created created an elemental force from the emptiness and light. \"And so the powers hovered in",0,30,1);
            base.books.AddEnglishBookText("the emptiness, which was now fulfilled with the five harmonious thoughts:\n \nThe Harmony of Stone hovered over the Harmony of Flame.\nThe Harmony of Water hovered over the Harmony of Stone.",0,30,1);
            base.books.AddEnglishBookText("The Harmony of Air hovered the Harmony of Water.\nThe Harmony of Fire hovered over the Harmony of Air.\nAnd behold: The four Harmonies formed a holy circle, and were infused by the Harmony of",0,30,1);
            base.books.AddEnglishBookText("Spirit, which sung with the thoughts and also filled the emptiness.\n \nAnd so the Five connected the Harmonies.\nUshara, Lady of the Harmonies of Stone, placed the Stone in the middle.",0,30,1);
            base.books.AddEnglishBookText("Tanora, Lady of the Harmonies of Water, placed the Water about the Stone.\nFindari, Lady of the Harmonies of Storms, placed the Air around the Water.\nBrágon, Lord of the Harmonies of Fire,",0,30,1);
            base.books.AddEnglishBookText("however placed the Fire within the Stone and beside the Stone, and so the came the Inside like the Outside, and so was the circle closed again.\n \n\"Eldan, Lord of the Thoughts, placed the spirit",0,30,1);
            base.books.AddEnglishBookText("in the center from the Inside and Outside: And so, he came through all the elements, as he came through the emptiness, and he filled them all with Thoughts.\"",0,30,1);
            base.books.AddEnglishBookText("From the Stone and the Spirit, the ancient Spirits of Stone were born.\nFrom the Water and the Spirit, the ancient Spirits of the Water were born.\nFrom the Air and the Spirit, the ancient Spirits",0,30,1);
            base.books.AddEnglishBookText("of Air were born.\nFrom the Fire and the Spirit, the ancient Spirits of Fire were born.\nFrom the emptiness and the Spirit, the ancient Spirits of Spirit were born.\n And they all guarded the new",0,30,1);
            base.books.AddEnglishBookText("world and served the creators in harmony and honor.\n \nAnd so the creators sent the old tribe of the ancient ancestors, the Aemon-Aeld, to Illarion: And so they placed themselves as their servants",0,30,1);
            base.books.AddEnglishBookText("in the world. And in the heads of the Aemon-Aeld were thoughts and emptiness, and they could embrace the emptiness and explore the Harmony, and so they spoke with the creators, and so they built",0,30,1);
            base.books.AddEnglishBookText("ithe first city.\"\n \n The elven creation mythology may be mostly similar to the Salkamaerian, although it tends to be worded in a very abstract manner.",0,30,1);
            base.books.AddEnglishBookText("~Orcish Creed~\nWorshipped Deities\n Brágon\n Malachín\n Zhambra\n Moshran\n Cherga\n The orcish religion—if one dares dub it such, for there are no churches or any solid structure devoted to it",0,30,1);
            base.books.AddEnglishBookText("eachother during summertime, and many rather rough sport disciplines originate from orcish traditions. The tribes meet regularly at these games, and female orcs choose their men. Autumn is the time",0,30,1);
            base.books.AddEnglishBookText("of gathering and hunting, and in winter is the time of the frost cold of the \"icy witch\", as the orcs oftenly name Cherga.\n The world of Illarion too finds itself in this cycle. It was once created",0,30,1);
            base.books.AddEnglishBookText("by the Gods out of bordeom, and thereupon the races were created and thrown upon it like dice, in order to evaluate who of them would be worthy of existance. Some of the dice and casting bones are",0,30,1);
            base.books.AddEnglishBookText("regarded by some of the tribes as sacred relics.\n The orcs hold their ancestors in honor. Wise councilors or leaders and mighty warriors are buried in \"ancestral caves\" upon death. The skull of the",0,30,1);
            base.books.AddEnglishBookText("deceased is kept in a stone niche, directly above his body, which is kept in a niche immediately underneath the other. Casting bones are crafted from his fingerbones and they are oftenly placed next",0,30,1);
            base.books.AddEnglishBookText("to the skull, with the reason of being able to inquire with the dead. By these means, orcish spirit summoners can ask the tribe's ancestors for advice or honor them.\n Malachin is a God of hunt and",0,30,1);
            base.books.AddEnglishBookText("battle to the orcs. Brágon is the sunbringer, lifegiver, and purge of all impurity and weakness. Zhambra is the \"Brother-God\"; he is called upon to slight quarrels or to close blood oaths (e.g.",0,30,1);
            base.books.AddEnglishBookText("battle frenzy to a warrior who has wisely sacrificed to him. However, he is also considered to be very fickle and easy to enrage—this is the reason why many orcs prefer to pray to Malachin before",0,30,1);
            base.books.AddEnglishBookText("an impeding battle, because in a battle frenzy of Moshran it is normally impossible to differentiate between friend and foe.\n In a spiritual sense, the strength of the soul is reflected by a strong",0,30,1);
            base.books.AddEnglishBookText("and spirit, and counts means alot to the orcs.\n In the orcish mythology, Cherga escorts the dead through the nothing, the eternal rift between the worlds, into the land of the ancestors. If an orc",0,30,1);
            base.books.AddEnglishBookText("has defeated many enemies in battle and was strong in his lifetime, his reputation there will be vast, and his ancestors will honor him. He will have many servants and slaves, and the woman will love",0,30,1);
            base.books.AddEnglishBookText("him. If he was weak and cowardly, he will spend his time there as a servant, perhaps even as a servant of a human, halfling, or dwarf.\n \n~Fire Orcs Creed~\nWorshipped Deities\n Brágon\n Malachín",0,30,1);
            base.books.AddEnglishBookText(" Zhambra\n \n The Orcs of the Flame date back to the time of the orc crusades, when many orcs came into capture by elves and sometimes by humans, and acquired much knowledge from the other races. As",0,30,1);
            base.books.AddEnglishBookText("ridiculous as this may sound at first, these generations have a creed that is a mixture of the traditional orcish, human and elven traditions; however they worship only those Gods to which they pray",0,30,1);
            base.books.AddEnglishBookText("for redemption.\n Fire orcs consider themselves a sort of orcish paladin. They dwell in the sweltering hot, somewhat volcanic areas of the Norbuk high plateau within stone fortresses. Their",0,30,1);
            base.books.AddEnglishBookText("settlements consist mostly of square stone houses, hewn from basalt and granite from the mountains. They live in a society of almost military-like organization, and they could be deemed fanatic in",0,30,1);
            base.books.AddEnglishBookText("their battle against all \"impurity\" (with which every demonic being or evil follower of Moshran is meant). Their creed unites human, elven, and orcish traditions:\n As such they believe that every",0,30,1);
            base.books.AddEnglishBookText("being is reborn so many times as necessary until it reaches perfection. Thus, perfection must be attained in life: Perfection of body, spirit, and society. For this reason, Fire orcs very rarely",0,30,1);
            base.books.AddEnglishBookText("leave their fortresses, because they regard all other forms of societies (amazingly enough, the traditional elven settlements are the exception) as imperfect, and as such, harmful for body and",0,30,1);
            base.books.AddEnglishBookText("spirit. Already from childhood on, they train their body in battle equally to their spirit in riddles, religion, poetry, and philosophy.\n Just like the elvenkind, they believe that long ago the world",0,30,1);
            base.books.AddEnglishBookText("was created by the elder five Gods, and that the eleven younger Gods were once mortals who attained absolute perfection.\n They regularly indulge in trade with other races and tribes and guard the",0,30,1);
            base.books.AddEnglishBookText("entrances to the high plateau. They ally with their \"wild\" brothers, the orcish clans, whenever injustices cause quarrel and strife between the tribes. Sometimes they even serve as diplomats to",0,30,1);
            base.books.AddEnglishBookText("represent the interests of a tribe or their fortresses with other races.\n Their religious views are considered to be slightly \"goofy, hur hur\" by the other tribes, although every one of these monks",0,30,1);
            base.books.AddEnglishBookText("and paladins garners enormous respect from those tribes at the same time. In the eyes of the other tribes, they are the chosen children of Brágon.\n A Fire orc will take any chance the Gods offer him",0,30,1);
            base.books.AddEnglishBookText("to return to the world, should he face untimely demise. This is why their kind erects many altars in the lands, while they attempt to bring civilization to their \"wild\" brothers and to commission",0,30,1);
            base.books.AddEnglishBookText("them at the same time. Sometimes it can happen that an outcast orc who managed to survive in wilderness for long enough is taken into their ranks after long trials, teachings and indoctrinations—",0,30,1);
            base.books.AddEnglishBookText("these sorts oftenly have the tendency to orive themselves in the eyes of the Gods and their new brothers, and travel the world to commit heroic deeds.\n Fire orcs believe that upon death they stand",0,30,1);
            base.books.AddEnglishBookText("before the \"Tribunal of Eternity\". Brágon will discern whether they lived for the purpose of the Holy Flame or not. Malachin will determine whether they fought honorfully or disciplined. And Zhambra",0,30,1);
            base.books.AddEnglishBookText("will weigh out whether they were loyal to their friends and obedient to their leaders or not. Then the three do council, and impart judgement upon him. If he failed their expectations, he must return",0,30,1);
            base.books.AddEnglishBookText("to the world of Illarion, to try his luck there again—his time has not yet come, because he has yet more tasks to complete. Should he have failed with complete intention and have acted out of despise,",0,30,1);
            base.books.AddEnglishBookText("he shall be punished or even immolated and molten by the Eternal Flame to return being used in the cycle of nature. Should he have met the expectations, he may enter the afterlife, to camp there and",0,30,1);
            base.books.AddEnglishBookText("drink with old friends. And after many centuries of celebrating and carouse they can return together to the world, to continue striving for perfection.\n \n~Blood Orcs~\n",0,30,1);
            base.books.AddEnglishBookText("(Note: This is not a religious description! This has yet to be extracted and placed within the racial descriptions of orcs as a supplemental.)\n \n The Blood orcs usually possess hardly any",0,30,1);
            base.books.AddEnglishBookText("intelligence whatsoever. They dwell upon the world only as bloodthirsty fighting machines, whose sole purpose consists of shedding as much blood in battle as utmost possible—from the moment on when",0,30,1);
            base.books.AddEnglishBookText("their comrades release them from their chains. One might add, they are completely indifferent about whether the blood is from friend or from foe when this occurs. In a battle, they are normally",0,30,1);
            base.books.AddEnglishBookText("simply turned in a certain direction and told \"Kill THERE.\" They hardly understand anything more than just that. Blood orcs are orcish kin that are \"stuck\" in a battle frenzy. The most of them",0,30,1);
            base.books.AddEnglishBookText("oftenly very short, because at practically every occasion they leap wildly into battle and are put into the first rows by their kin. They live in a nearly constant battle frenzy and are barely capable",0,30,1);
            base.books.AddEnglishBookText("of forming a single clear thought. They give in completely to their instincts or are even downright controlled by them. Some of those who were even too wild for their own tribes, are either",0,30,1);
            base.books.AddEnglishBookText("constantly chained down like watchdogs, or live free like predators out in the wilderness.\n The \"philosophy\" of a Blood orc—which only surfaces in very calm times, as when such a subject is even",0,30,1);
            base.books.AddEnglishBookText("capable of forming any thought—is to either kill as many enemies as possible, or attempt to become \"normal\" again. That is the entire compass of their \"religion\": They either pray to Cherga for their",0,30,1);
            base.books.AddEnglishBookText("heated temper to cool down, or to Zhambra, so that he allows them to recognize their friends; or they pray to Moshran, so that he lets them die in a glorious battle and quantifies their blood rush to",0,30,1);
            base.books.AddEnglishBookText("such astonishing heights that they can sow death among countless enemies. A former orcish general who was \"stuck\" in a blood rush and thus became a Blood orc, was said to have spoken the following in",0,30,1);
            base.books.AddEnglishBookText("one of his calm moments:\n \"I think not, for I am dead already. From my axe drips the blood of the slain, and in my burning veins pulses pure fury of Gods. I pray for redemption from this pain and",0,30,1);
            base.books.AddEnglishBookText("from this— DON'T STARE AT ME LIKE THAT— RRRAAAAARRGH !\"\n Blood orcs who \"survived\" this state of constant frenzy and brought their instincts back under control, most oftenly only succeeded therein",0,30,1);
            base.books.AddEnglishBookText("with under the guidance and aid of a shaman, warlock, or healer.\n \n~Lizardfolk~\nWorshipped Deities\n Zelphia\n The religions of the lizardfolk acknowledges other Gods but worships only one deity",0,30,1);
            base.books.AddEnglishBookText("but worships only one deity exclusively: Tanora, the Goddess of Water. Her name in the tongue of the lizardfolk is different though and sounds like \"Zsshel-phey-aarh\" (in simplification: \"Zelphia\").",0,30,1);
            base.books.AddEnglishBookText("In canon to their mythology, the lizardfolk descends from Tanora directly. Their creation myths have striking similarities with those of the elvenkind. The other Gods are however Gods of the other",0,30,1);
            base.books.AddEnglishBookText("races and as such, of hardly any significance to the lizardfolk. Even if a lizard does not want to enrage them and respects their followers, he will not pray to them.\n Lizardfolk believe that they must",0,30,1);
            base.books.AddEnglishBookText("pass the ocean upon death by swimming and diving through it. Had they lived honorfully, humble, and for the designs of the Goddess, then the ocean shall be calm for them. They will find some tasty",0,30,1);
            base.books.AddEnglishBookText("fish along the way and arrive with sound travel and without harm at the Islands of Eternity.\n Dishonarability, arrogance and selfishness in life will tie a stone to them for each and every atrocity,",0,30,1);
            base.books.AddEnglishBookText("and each stone must be delivered to the Islands of Eternity. Every stone draws them closer to the ground of the ocean and thus burdens their progression, because upon the floor of the ocean is a",0,30,1);
            base.books.AddEnglishBookText("scattered maze of valleys, rocks, and mountains to be arduously traversed. It could also occur that the ocean itself plays \"tricks\" on them, in shape of some annoying squids or fire-jellyfish, for",0,30,1);
            base.books.AddEnglishBookText("for example. Those lizards who are burdened merely with small stones or no stones at all may opt to grant fellow carriers mercy and take a single stone from them.\n In contrary to the elvenkind, the",0,30,1);
            base.books.AddEnglishBookText("lizardfolk does not believe in reincarnation. Altars of the Gods as well as sacred rituals are the only way for them to return from the Islands of Eternity to Illarion, according to their creed.",0,30,1);
            base.books.AddEnglishBookText("However, Zelphia grants all the returners a favor: So they do not tear themselves apart in life out of pure desire for the beauty of the Eternal Islands, the Goddess ties back their memories of the",0,30,1);
            base.books.AddEnglishBookText("land of the dead.\n \n~Halflings Creed~\nWorshipped Deities\n Oldra\n Adron\n Elara\n Ronagan\n Sirani\n Zhambra\n Nargun\n \n The myths of the halflings are truly remarkable. As an introduction",0,30,1);
            base.books.AddEnglishBookText("to their creation mythologies it is said: \"In the beginning, the creators created this world from a pumpkin. Then they created the Gods from cherry seeds. After all this work they departed from",0,30,1);
            base.books.AddEnglishBookText("Illarion, to rest and celebrate their endeavors with food and drink. It was too much work for the Gods to till all the fields, so they took in turn corns of sand and created the races of humans and",0,30,1);
            base.books.AddEnglishBookText("halflings. In order for someone to harvest the fruits of the water, they created the lizardfolk, and so the forest's growth would not stay unpicked, they created the elves. The dwarves were created to",0,30,1);
            base.books.AddEnglishBookText("trade the fruits of the mountain for edible fruit. The orcs however were created after the unnamed one (whom some also call the Milkspiller) suscepted a sickness in his stomach from a cake he had",0,30,1);
            base.books.AddEnglishBookText("stolen from Nargûn, who had wanted to use it in order to slay a giant...\"\n Halflings believe that someone who is dead is admitted to paradise if that someone lived with good intentions, just, and",0,30,1);
            base.books.AddEnglishBookText("and contently. Cakes and roast beef grow on the trees and bushes, beer, sweetened milk, exceptional wine of various sorts and also clear water flow from several different springs; and there are no",0,30,1);
            base.books.AddEnglishBookText("flies or ants that assault a picknet basket—except of course for the exceptionally big types that some consider a delicatessen.\n Celebrations are constantly kept going there and there is hardly",0,30,1);
            base.books.AddEnglishBookText("anything to be frustrated about because everybody can go anywhere their hearts desire.\n Evil beings however do not reach the paradise. Their entrance to it is denied until they have honestly and",0,30,1);
            base.books.AddEnglishBookText("sincerely bettered themselves. And they may only then lives there when they have excused themselves to anybody they had done wrong in their life.\n Halflings believe that life should be most possibly",0,30,1);
            base.books.AddEnglishBookText("enjoyed and made the most of. The idea of a paradisical afterlife is more like a guideline or goal of theirs that they should already reach within lifetime. Those who were able to enjoy a long life",0,30,1);
            base.books.AddEnglishBookText("and keep themselves and their family out of trouble garner alot of respect from their fellow kin.",0,30,1);

            ---------
            
            base.books.AddLanguage("common language",2); 

            base.books.AddEnglishBookText("\n \n Cultures of the Humans",0,0,2); 
            base.books.AddEnglishBookText("\n \n~Introduction~",0,0,2); 
            base.books.AddEnglishBookText("The human cultures are vast and various. Though, many humans are unaware of their history.",0,0,2); 
            base.books.AddEnglishBookText("The human societies hold many different ethics and social orders. Aside from nomadic cultures, most cilivizations are built near roads and streams for easy trading inside the race.",0,0,2); 
            base.books.AddEnglishBookText("The aforementioned cultures are more of a historical definition, and true forms of certain cultures may be hard to find in these modern days.",0,0,2); 
            base.books.AddEnglishBookText("\n \n ~Primitive Cultures~",0,0,2); 
            base.books.AddEnglishBookText("Seafolk (Norodaj)\n \n People of this culture mainly live in cold, rough climates. To compensate for the cold weather they grow large beards and dress themselves in thick furs and pelts.",0,0,2);
            base.books.AddEnglishBookText("Their lands are usually found inbetween mountains and sea. Therefore, they mainly would trade with dwarves and lizards.",0,0,2); 
            base.books.AddEnglishBookText("Along with trading, they have adopted some culture from the dwarves. Their alphabet is somewhat similar to dwarven runes. They usually know the common tongue and their own dialect.",0,0,2); 
            base.books.AddEnglishBookText("They usually eat fish from the nearby sea or herd sheep on what small fields they have. They tend to travel in long boats, skimming the shore for fish.",0,0,2); 
            base.books.AddEnglishBookText("For religion, they tend to adopt the Gods of their nearby culture. They often pray to Irmorom, Tanora, and Malachin.",0,0,2); 
            base.books.AddEnglishBookText("Because of their simple lives, they often fear the use of magic as it is a rare sight to see, and due to superstitions, they fear elves for they affinity to magic.",0,0,2); 
            base.books.AddEnglishBookText("In terms of social order, there is a clan leader. He is decided to lead through a contest where they test wits and skill in fighting. The leader will be challenged when he becomes old enough.",0,0,2); 
            base.books.AddEnglishBookText("The leader also has special priviledges. Leaders and previous leaders may have up to three wives, while normal citizens can only wed one.",0,0,2); 
            base.books.AddEnglishBookText("Marriage is considered to be a life time commitment. Any betrayal of it is considered a horrible act. If a man mistreats his wife or children, he is soon exiled from the society.",0,0,2); 
            base.books.AddEnglishBookText("If one dies as a member, their bodies will be burnt to ash in a ritual by a holy shaman. They will then be sprinkled out on the shore.",0,0,2); 
            base.books.AddEnglishBookText("Plainfolk (Serinjah) \n \nThe People of Serinjah live on sprawling plains. With great herding capabilities, they often raise horse and cows. They would build small wooden houses to form a loose village.",0,0,2);
            base.books.AddEnglishBookText("Since they are around horses, they are known as great riders, tamers, hunters, and tamers. They are often proficient in spears, bows, long swords, throwing stars, and throwing knives.",0,0,2); 
            base.books.AddEnglishBookText("Along with weapons, they have developed their own hand to hand combat. They often wear hard leather or wooden armor and are seldom seen in large cumbersome metal armor, as they need flexibility.",0,0,2); 
            base.books.AddEnglishBookText("The Plainfolk can speak the common talk, a small bit of elvish, and their own ancestral language.",0,0,2); 
            base.books.AddEnglishBookText("They usually worship Malachin since they are often on hunts, but they pray to the other Younger Gods as well.",0,0,2); 
            base.books.AddEnglishBookText("Their social order is as such. They are lead by three people. They consist of a young man, a young amazonian woman, and a much older woman. The Plainfolk find power in the land and many are skilled druids.",0,0,2); 
            base.books.AddEnglishBookText("As well as druids, there are simple mages within the society. The Serinjah to not practice conventional marriage, but do form lasting bonds. Dead people are buried in graves marked with small stones.",0,0,2); 
            base.books.AddEnglishBookText("Plainfolk (Ama-shoon) \n \nThe members of the Ama-shoon culture live in forests near the plains of the Serinjah. The society is composed entirely of females and children under four.",0,0,2); 
            base.books.AddEnglishBookText("When the male children grow older than four years they are taken to their fathers of a Serinjah tribe. Ama-shoons live like and sometimes with Serinjah, but their loyalty remains to their land.",0,0,2); 
            base.books.AddEnglishBookText("They often follow Findari. They are experts with spears, slings, swords, bows, and daggers. They mostly prefer hard leather or wood armor for flexibility.",0,0,2); 
            base.books.AddEnglishBookText("The ama-shoon are proficient when unarmed, and ride horses barebacked. They are lead by one female leader who is often a priest of Findari. They tend to use agressive and healing magic.",0,0,2); 
            base.books.AddEnglishBookText("\n \n ~Civilized Cultures~",0,0,2); 
            base.books.AddEnglishBookText("Lightfolk (Salkamaerian) \n \nThe Salkamaerians live in towns, villages, and castles. Common language originated in this culture, in the first Salkarmaerian city called 'Salkamar'.",0,0,2); 
            base.books.AddEnglishBookText("Loyalty, honor, and respect are the ethics of Salkamaerian culture. All children learn reading and writing. Slavery is forbidden by law and a military education is offered for free for the needy.",0,0,2); 
            base.books.AddEnglishBookText("Salkamaerian kings and nobles are more of a representative of the people. Cities are ruled by male and female alike. The culture is open to strangers.",0,0,2); 
            base.books.AddEnglishBookText("Poets, bards, and philosophers are well known, as are their paladins. Zhambra, Oldra, Sirani, Elara, and Malachin are the often worshiped in this culture.",0,0,2); 
            base.books.AddEnglishBookText("Straightfolk (Albarian) \n \nAlbarians live in small towns, villages, and castles. They are ruled by male nobles, which own all of the Albarian land. Most of the citizens are bondsmen.",0,0,2); 
            base.books.AddEnglishBookText("The bondsmen can be called upon to add its respective noble. Those who do not obey their orders are punished harshly. As well, Albarian females belong to their husbands or fathers.",0,0,2); 
            base.books.AddEnglishBookText("Female Goddesses are forbidden in this culture.",0,0,2); 
            base.books.AddEnglishBookText("The Albarian culture started in the city of Albar, as it is a little older than the origin of Salkamar. Since bondsmen often rebel, Albarian towns are getting seldom.",0,0,2); 
            base.books.AddEnglishBookText("Freefolk (Gynkeesh) \n \nGynkeesh people live in independent towns, villages, and castles. Slavery is forbidden, but sometimes people tend to overlook this law. They are ruled by their free market.",0,0,2); 
            base.books.AddEnglishBookText("The armies consist of paid mercenaries. Poor people often live in slums, and thieves are rampant there. Most villages are of mixed races. Gynkeesh villages are often refered as \"junkyards\".",0,0,2); 

            base.books.AddGermanBookText("\n \n Kulturgeschichte des Menschen",0,0,2); 
            base.books.AddGermanBookText("\n \n~Einführung~",0,0,2); 
            base.books.AddGermanBookText("Es gbt eine ungeheure Zahl und Vielfalt an Menschenvölkern, doch viele Menschen sind sich der eigenen geschichtlichen Ursprünge kaum bewusst.",0,0,2); 
            base.books.AddGermanBookText("Die Gesellschaften der Menschen kennen viele verschiedene Sittenlehren und Gesellschaftsordnungen. Neben einigen nomadisierenden Völkern, haben sich die meisten Zivilisationen entlang der Verkehrswege und Ströme niedergelassen.",0,0,2); 
            base.books.AddGermanBookText("Die zuvorgenannten Kulturen verstehen sich eher als historische Einteilung,und in unseren modernen Zeiten werden ursprüngliche Formen kaum noch zu finden sein.",0,0,2); 
            base.books.AddGermanBookText("\n \n ~Primitive Kulturen~",0,0,2); 
            base.books.AddGermanBookText("Seevolk (Norodaj)\n \n Ihre Angehörigen sind die Bewohner kalter und unwirtlicher Klimagebiete. Um der kalten Witterung zu trotzen, lassen sie sich lange Bärte stehen und kleiden sich in dicken Fellen und Pelzen.",0,0,2);
            base.books.AddGermanBookText("Ihre Gebiete findet man in den Zonen zwischen Küste und Gebirge. Aus diesem Grund treiben sie auch hauptsächlich Handel mit Zwergen und Echsenmenschen.",0,0,2); 
            base.books.AddGermanBookText("Im Zuge dieses Handels haben sie einige Gewohnheiten der Zwerge übernommen. Ihr Alphabet kann mit dem zwergischer Runen verglichen werden. Neben dem eigenen Dialekt beherrschen sie für gewöhnlich auch die Gemeinsprache.",0,0,2); 
            base.books.AddGermanBookText("Diese Menschen essen viel Fisch aus dem nahen Meer und halten Schafe auf ihren kleinen Feldern. Sie reisen meist in Langbooten, mit denen sie die Küstengewässer nach Fisch abgrasen.",0,0,2); 
            base.books.AddGermanBookText("Die Norodaj-Religion neigt dazu, sich ihre Gottheiten bei benachbarten Kulturen zu entlehnen. Häufig beten sie zu Irmorom, Tanora, oder Malachin.",0,0,2);
            base.books.AddGermanBookText("Zu dem einfachen Lebenstils gehört auch die Angst vor der Anwendung von Magie, der man deshalb an der See auch nur selten begegnet, und infolge ihres Aberglaubens fürchten sie sich auch vor dem Elbenvolk, wegen dessen Hang zur Zauberei.",0,0,2); 
            base.books.AddGermanBookText("Ihre Gesellschaftsordnung kennt den Begriff des Sippenoberhauptes. Er wird im Rahmen eines Wettkampfes auserkoren, bei dem die Kampfeskunst entscheidet. Wenn ein Anführer in die Jahre kommt, wird er zum Wettkampf herausgefordert.",0,0,2); 
            base.books.AddGermanBookText("Als Anführer hat man auch Privilegien. Als Chef oder als ehemaliger Anführer darf man bis zu drei Ehefrauen haben, gemeinen Bürgern wird nur eine Ehefrau zugestanden..",0,0,2); 
            base.books.AddGermanBookText("Die Ehe wird als lebenslange Verpflichtung angesehen. Jeglicher Verrat daran wird als furchtbare Tat geahndet. Misshandelt ein Mann Frau oder Kinder, wird er sofort aus der gemeinschaft ausgestoßen.",0,0,2); 
            base.books.AddGermanBookText("Stirbt man als Angehöriger, wird der Leichnam in einem festgelegten Ritus vom Schamanen verbrannt. Die Asche wird anschließend an der Küste verstreut.",0,0,2); 
            base.books.AddGermanBookText("Das Volk der Ebenen (Serinjah) \n \nDas Volk der Serinjah lebt in den weithingetreckten Ebenen. Aufgrund der großen Flächen züchten sie häufig Pferde und Kühe. Aus kleinen Holzhäusern errichten sie lockere Dorfgemeinschaften.",0,0,2); 
            base.books.AddGermanBookText("Durch den Umgang mit Pferden sind sie als großartige Reiter bekannt, ebenso als Tierbändiger und als Jäger. Viele sind im Umgang mit Speeren geübt, mit Pfeil und Bogen, ebenso mit dem Langschwert, mit Wurftstern und -Messer.",0,0,2); 
            base.books.AddGermanBookText("Neben der Waffenkunst haben sie auch den waffenlosen Kampf weiterentwickelt. Viele tragen Hartleder- oder Holzrüstungen, selten sieht man sie dagegen in den unhandlichen Metallpanzern, weil sie eben Flexibilität benötigen.",0,0,2); 
            base.books.AddGermanBookText("Das Volk der Ebenen beherrscht sowohl die Gemeinsprache, etwas elbisch, sowie ihre eigene, urtümliche Sprache.",0,0,2); 
            base.books.AddGermanBookText("Für gewöhnlich huldigen sie Malachin, schon wegen ihrer Liebe zur Jagd, aber sie beten auch zu einigen der jüngeren Gottheiten..",0,0,2); 
            base.books.AddGermanBookText("Ihre Gesellschaftsordnung sieht drei Führer vor, bestehend aus einem jungen Mann, einer Kriegerin und einer Greisin. Das Volk der Ebenen schöpft seine Kräfte aus der Natur und es gibt viele qualifizierte Druiden.",0,0,2); 
            base.books.AddGermanBookText("Neben Druiden gibt es in der Gemeinschaft auch schlichte Magier. Die Serinjah kennen keine Ehe im herkömmlichen Sinne, sondern eine Art dauerhafte Liebesbande. Tote werden in Gräbern beigesetzt, die mit kleinen Steinen gekennzeichnet werden.",0,0,2); 
            base.books.AddGermanBookText("Das Ebenenvolk der Ama-shoon \n \nDie Angehörigen der Ama-shoon leben in den Wäldern nahe der von den Serinjah bevölkerten Ebenen. Diese gesellschaft besteht ausschließlich aus Frauen und Kindern unter vier Jahren.",0,0,2); 
            base.books.AddGermanBookText("Wenn männliche Nachkommen das vierte Lebensjahr überschritten haben, werden sie ihren Vätern aus einem Serinjah-Stamm überantwortet. Ama-shoons leben wie undmanchmal mit den Serinjah, halten aber stets ihrem eigenen Volk die Treue.",0,0,2); 
            base.books.AddGermanBookText("Man verehrt zumeist Findari. Im Umgang mit Speer, Schleuder, Schwert, Bogen und Dolch meisterhaft, ist der hartlederne Panzer oder die Holzrüstung wegen ihrer Beweglichkeit die bevorzuge Kampfkleidung.",0,0,2); 
            base.books.AddGermanBookText("Die Ama-shoon sind auch unbewaffnet sehr geschickt und reiten ihre Pferde ohne Sattel. Sie werden von einer Frau angeführt, die oftmals auch als eine Priesterin der Göttin Findari dient. Sie neigen zur Anwendung von agressiver, aber auch von heilender Magie.",0,0,2); 
            base.books.AddGermanBookText("\n \n ~Hochkulturen~",0,0,2); 
            base.books.AddGermanBookText("Das Lichtvolk (Salkamaerian) \n \n Salkamaerier leben in Städten, Dörfern und Burgen. Die Gemeinsprache wurde hier entwickelt, in der ersten Salkarmaerianischen Stadt, die 'Salkamar' genannt wird.",0,0,2); 
            base.books.AddGermanBookText("Treue, Ehre und allseitige Achtung sind Grundwerte der Salkamaerianischen Kultur. Alle Kinder lernen lesen und schreiben. Die Sklaverei ist gesetzlich verboten und den Mittellosen wird eine kostenlose militärische Ausbildung angeboten.",0,0,2); 
            base.books.AddGermanBookText("Salkamaerianische Könige und Adlige sind die Repräsentanten des Volkes. Städte werden gleichermaßen durch Frauen wie durch Männer regiert. Fremden steht die Gesellschaft mit Offenheit gegenüber.",0,0,2); 
            base.books.AddGermanBookText("Dichter, Sänger und Denker sind weithin bekannt, ebenso ihre Paladine. Zhambra, Oldra, Sirani, Elara, und Malachin sind die gebräuchlichen Angebetenen dieser Gesellschaft.",0,0,2); 
            base.books.AddGermanBookText("Das \"aufrechte\" Volk (Albarian) \n \nAlbarianer leben in kleinen Dorfgemeinschaften, und auf Burgen. Regiert von einem männlichen Adligen, dem auch das umliegende Land gehört. Die meisten der Einwohner sind Leibeigene.",0,0,2); 
            base.books.AddGermanBookText("Die Leibeigenen können aufgefordert werden, ihren Adligen Ehrerbietungen zu erbringen. Wer sich dabei nicht fügt hat schwere Strafen zu erwarten. Ebenso werden Frauen als Eigentum ihrer Ehemänner oder der Väter angesehen.",0,0,2); 
            base.books.AddGermanBookText("Diese Gesellschaft duldet keine weiblichen Gottheiten.",0,0,2); 

            --------------------------------------
            
            base.books.AddLanguage("common language",3);

            base.books.AddEnglishBookText("The Epos of the Grey Ones\nThe true story of a heroic battle\nBy Nerian Finera, priest of Malachín",0,5,3);
            base.books.AddEnglishBookText("At the castle called refuge,\nKnights met to travel out,\nTheir braveness has been very huge,\nIn fight they had no doubt.",0,5,3);
            base.books.AddEnglishBookText("So they left their beloved home,\nDivine help they had,\nThrough the forest they would roam,\nWith armours they were clad.",0,5,3);
            base.books.AddEnglishBookText("Out they went to foreign grounds,\nSinging they were on their way,\nFrom their mouths came lovely sounds,\nVigilant they watched for prey.",0,5,3);
            base.books.AddEnglishBookText("Bright in light the weapons shone,\nThey carried on their backs,\nFear for battle they had none,\nThe sun set behind their necks.",0,5,3);
            base.books.AddEnglishBookText("When at the field the knights arrived,\nDragons showed their flaming breath,\nInto war the brave ones dived,\nThey would fight to death.",0,5,3);
            base.books.AddEnglishBookText("Soon blades were hitting scales,\nArrows shot right through the air,\nBraveness never fails,\nThe knights were striking only fair.",0,5,3);
            base.books.AddEnglishBookText("Triumph has been close,\nWhen from the lifeless earth,\nAn undead horde arose,\nBy magic given birth.",0,5,3);
            base.books.AddEnglishBookText("The Grey Rose from the heavy rock,\nStrove hard to gain the victory,\nThe enemies they would mock,\nBeaten the opponents would be.",0,5,3);
            base.books.AddEnglishBookText("Although the knights fought for every yard,\nBack to the bridge they had to retreat,\nThat brave group did not fall apart,\nKept together by their creed,",0,5,3);
            base.books.AddEnglishBookText("And just as flames, that reach for sky,\nThe knights started to retaliate,\nThe dragon soon was to die,\nHit by justice's blade.",0,5,3);
            base.books.AddEnglishBookText("There was no time for respite,\nNoone could take a pause,\nFor some goblins wished to fight,\nAngry for an unknown cause.",0,5,3);
            base.books.AddEnglishBookText("So another battle was begun,\nGoblin blood was spilt,\nThe little creatures had to run,\nThe brave knights were thrilled.",0,5,3);
            base.books.AddEnglishBookText("A mild breeze stroked over the plain,\nWounded on their side were hardly any,\nNoone died in vain.\nFoes they had killed many.",0,5,3);
            base.books.AddEnglishBookText("With heads up high the knights set out,\nTheir swords they again had sheathed,\nTheir expressions have been proud,\nFrom oblivion their names shall always be retrieved.",0,5,3);
            base.books.AddEnglishBookText("When once you face the big stronhgold,\nAnd for asylum you seek,\nThe land outside is bitter cold,\nThen keep kindness on its peak.",0,5,3);
            base.books.AddEnglishBookText("And when you're entering from outside,\nThen do not hold from singing,\nLet them hear this song of pride,\nFor hope with that you might be bringing. ",0,5,3);

            base.books.AddGermanBookText("Das Epos der Grauen\nDie wahre Geschichte einer heroischen Schlacht\nvon Nerian Finera, Priester Malachíns",0,5,3);
            base.books.AddGermanBookText("In der Burg, Zuflucht genannt,\nTrafen sich Ritter, um auszuziehen.\nWeil die Kampfeslust sie ausgesandt,\nDie Ritter suchten nicht zu fliehen.",0,5,3);
            base.books.AddGermanBookText("So verließen sie alsbald ihr Heim,\nIn der Gunst der Götter standen sie,\nIn den Seelen von Angst kein Keim,\nEine Schlacht zu schlagen, wie noch nie.",0,5,3);
            base.books.AddGermanBookText("Frohen Mutes gen ferne Lande,\nSingend des Weges sie gingen,\nAuf dass Schwert auf Schuppe brande,\nWollten sie mit Monstern ringen.",0,5,3);
            base.books.AddGermanBookText("Im Licht glänzten stählern die Rüsten,\nDie am Leibe sie trugen,\nSich ihrer noblen Herkunft zu brüsten,\nWenn sie mit Feinden sich schlugen.",0,5,3);
            base.books.AddGermanBookText("Als zum Felde sie gelangten,\nEmpfingen brüllend sie Drachen,\nDie Bösen um ihr Leben bangten,\nStarrend in des Todes Rachen.",0,5,3);
            base.books.AddGermanBookText("Bald hörte man die Klingen scharren,\nDie Pfeile zischten von den Sehnen,\nDie Feinde mochten nicht verharren,\nNur rasch das Ende sich ersehnen.",0,5,3);
            base.books.AddGermanBookText("Der Sieg war nicht mehr weit,\nDa brach plötzlich aus der Erde,\nGegen jedes ehern’ Schwert gefeit,\nEine zorn'ge untot’ Herde.",0,5,3);
            base.books.AddGermanBookText("Die Graue Rose vom schroffen Stein\nWand sich wild im Winde,\nDer Sieg würd' doch noch ihrer sein,\nDass der Feind Triumph nicht finde.",0,5,3);
            base.books.AddGermanBookText("Sie bestritten jede Fußesbreite\nWurden bald zur Brücke schon gedrängt,\nNicht einer sucht’ das Weite,\nUnd wie der Wolf den Hasen fängt,",0,5,3);
            base.books.AddGermanBookText("Lockten sie Feinde in die Falle,\nVersperrten rasch den Rückweg ihnen,\nDer Drache streckte gleich die Kralle,\nMächtig gar die Ritter schienen.",0,5,3);
            base.books.AddGermanBookText("Noch eh' der Jubel sich erhob,\nKeiner Zeit zum Atmen fand,\nKam ein Kobold, klein und grob,\nHinter ihm sein Stamm schon stand.",0,5,3);
            base.books.AddGermanBookText("Sie fordert’ eilends eine Schlacht,\nDie wüste, starke Koboldschar,\nDie war allzu schnell gemacht,\nEin rasches Schauspiel bot sich dar.",0,5,3);
            base.books.AddGermanBookText("Ein lauer Wind fuhr durch die Ähren,\nKaum ein Opfer zu beklagen,\nDie Grauen wünschten zurückzukehren,\nNachdem die Schlacht geschlagen.",0,5,3);
            base.books.AddGermanBookText("Gehob’ner Häupter brachen sie auf,\nDie Klingen in den Scheiden,\nFolgten erschöpft der Straße Lauf,\nDie Räuber würd’n sie meiden.",0,5,3);
            base.books.AddGermanBookText("Wenn dereinst Ihr vor der Feste steht,\nUnd frierend um Einlass ihr bittet,\nEine steife Brise draußen weht,\nSo verhaltet Euch gesittet.",0,5,3);
            base.books.AddGermanBookText("Tretet ein Ihr in die Hallen,\nDann singet laut und guter Dinge,\nLasst dieses kühne Lied erschallen,\nAuf dass es überall erklinge.",0,5,3);
            
            --------------------------------------
            
            base.books.AddLanguage("common language",4);
            
            base.books.AddEnglishBookText("How to Be A Simple Monk\nby Logrin Parr",0,20,4);
            base.books.AddEnglishBookText("Table of Contents\n\nAcknowledgements\nChapter one : What is a monk?\nChapter two : Look the part\nChapter three : Monkly duties",0,20,4);
            base.books.AddEnglishBookText("Acknowledgements\n\nI dedicate this book to the ladies",0,20,4);
            base.books.AddEnglishBookText("Chapter One - What is a Monk?\n\nMany people ask me what my vocation is, or to put on some pants. To both questions I anwser that I am but a simple monk. For that is what I truly am. At this point you",0,20,4);
            base.books.AddEnglishBookText(" should probably be wondering how a blind man has managed to write the book you are holding in your hands, or why I wouldn't have any pants on in the first place. It's best not to dwell on such things",0,20,4);
            base.books.AddEnglishBookText(" So by now your asking yourself..what is a monk? Or you've put down the book and walked outside because the sun is shining. On the off chance your still reading I will explain in detail what a monk ",0,20,4);
            base.books.AddEnglishBookText("is\n\nAwsome\n\n It might seem self indulgent but after making a short list of things that have happened to me in the last dwarven week I realized that no other word quite sums it up as nicely. For ",0,20,4);
            base.books.AddEnglishBookText(" For you benifit I have included that list\n\n1. Did not need to spend a single copper at the tavern to become inebriated beyond coherent speech\n2. Saved a shop by putting out a towering inferno with",0,20,4);
            base.books.AddEnglishBookText("buckets of freezing water\n3. Set a shop ablaze ( prior to number 2 )\n4. Made an orc sing for a new bag\n5. Did not give that orc the bag",0,20,4);
            base.books.AddEnglishBookText("6. Threw a bag into the fire because I had too many\n7. Went fishing for zombies\n8. Attacked a hulking black bird with throwing stars while it smoked a pipe\n9. Had an angry Lizardess toss lamps at ",0,20,4);
            base.books.AddEnglishBookText("me\n10. Wrote a book despite being blind\n\n Now take your index finger and point at random at one of the aforementioned activities. There is a seven to ten chance I was naked for that.",0,20,4);
            base.books.AddEnglishBookText("At this point I'm sure you agree that I am simply awe inspiring and a god among men, so slap yourself for being so blasphemous. I'm dead serious, go ahead the book will still be here when your done.",0,20,4);
            base.books.AddEnglishBookText(" Good. Now that that's out of the way you definately want to be as fantastic as I am. So to better facilitate that desired outcome read the rest of the book naked.",0,20,4);
            base.books.AddEnglishBookText("Chapter Two - Look the Part\n\n So now that your naked it's the perfect starting point for becoming a simple monk. After all, if you don't look like a monk people will think your some sort of loon",0,20,4);
            base.books.AddEnglishBookText("for punching a troll in the face.\n\n The most important thing to note is not to wear a robe. Preists where robes, and preist are definately not what you want to look like. After all the only thing ",0,20,4);
            base.books.AddEnglishBookText("seperating a preist from a raving lunitic on the street corner is a robe. So unless you want to brabble unintelligently and rent out cows to people avoid robes at all costs",0,20,4);
            base.books.AddEnglishBookText(" There are two ways to dress like a monk, it really just come down to personal preferance in the end when deciding which one is best for you. I have seperated them for your conveinance",0,20,4);
            base.books.AddEnglishBookText(" Method One: Take three seperate outfits that vary widely in style of dress. For example a rangers leathers, a silken set of regals and plate mail. Take the acquired assortment of material and have an",0,20,4);
            base.books.AddEnglishBookText("orc smash it with a hammer. Gather up the biggest remnents from the pile and start peicing them togather at random. The items mentioned prior when combined could be considered to be a rangers silken ",0,20,4);
            base.books.AddEnglishBookText("plate mail. And also to be an eye sore. Don't worry that's the look your going for.",0,20,4);
            base.books.AddEnglishBookText("Method Two: Simply remain naked and exit the library",0,20,4);
            base.books.AddEnglishBookText("Chapter Three - Monkly Duties\n\nBeing a monk isn't all fun and games, people expect certain things from you. You don't want to disappoint your adoring public do you? So the fact of the matter is you ",0,20,4);
            base.books.AddEnglishBookText("have to adhere strickly to a set of principles.\n\n1. Be Personable",0,20,4);
            base.books.AddEnglishBookText("Remember your main source of income is throw to you by passing town folk, even if you despise someone and the way they keep shrugging and readjusting their bandana just chuckle and remain calm. Also",0,20,4);
            base.books.AddEnglishBookText("if you are friendly to absolutely everyone it provides a nice smoke screen for \"accidently\" letting your hands wander when you hug a particularly well endowed knight. Friendly old Mr. Parr would never",0,20,4);
            base.books.AddEnglishBookText("do such a thing though, just a passing notion ladies.\n\n2. Learn to Chuckle",0,20,4);
            base.books.AddEnglishBookText("Monks do not laugh, we do not guffaw, titter, giggle or snicker. Monks chuckle. This was set in stone so long ago that it's origins are unknown to even the first monks. If you find you have broken",0,20,4);
            base.books.AddEnglishBookText(" this sacred covenent you have to eat a hand full of ash. I'm not making this up just so people will eat ash either. \n\n3. Have a Tradgic Story to Tell",0,20,4);
            base.books.AddEnglishBookText("My life was a inescapable vortex of soul shattering pain, so I had it easy. If you don't have a sob story to tell you'll lose alot of income. After all would you give your hard earned money to someone",0,20,4);
            base.books.AddEnglishBookText("who grew up in a money orchard farming puppies and rainbows? To avoid this uncomfortable scenario start cutting yourself whenever someone asks about your past then tell them you don't want to talk",0,20,4);
            base.books.AddEnglishBookText("about it. Most people will asume you had it rough.\n\n4. Be an expert",0,20,4);
            base.books.AddEnglishBookText("People expect a monk to be worldly, so if someone asks you something and you can't supply an anwser you've just shattered their hopes and dreams like they were an orc trying to make blow glass. Don't ",0,20,4);
            base.books.AddEnglishBookText("worry though just remember this simple rule; If they're asking they don't know either. So just make something up they won't be able to tell the difference. Keep in mind someone listening in may",0,20,4);
            base.books.AddEnglishBookText("correct you, to avoid this simply take the person aside and whisper your fabrication in hushed voice. They'll feel like your imparting some great secret and feel special. And happy people are usually ",0,20,4);
            base.books.AddEnglishBookText("generous when panhandled to.\n\n5. Be wise",0,20,4);
            base.books.AddEnglishBookText("Wisdom is something that a monk must exude. To do so you need three things\n1. Someone to hear your wisdom\n2. A pipe\n3. Sibanc\nWith these three things wisdom will follow",0,20,4);
            base.books.AddEnglishBookText("6. Get a Familliar\n Having an exotic animal with you at all times will make you seem mysterious and otherworldly, expecially if the animal is a unique color. If the animal is not pigmented any",0,20,4);
            base.books.AddEnglishBookText("different from others of it's ilk paint it as you desire. Don't worry about nature, your a monk not a druid",0,20,4);
            base.books.AddEnglishBookText("Examples of good familliars: Birds, Wolves, Snakes, Bears, Anything with Only One Eye, Turtles\nExamples of bad familliars: Bugs, Pigs, Cows, Inatimate Objects, Swarms of Insects, Orcs, Turtles",0,20,4);
            base.books.AddEnglishBookText("Turtles is listed twice because it really depends on the the turtle, sometimes they have bad attitudes\n\n7. Panhandeling\n Learning to beg shamelessly is pivitol to surviving as a monk. Just ask for ",0,20,4);
            base.books.AddEnglishBookText("money and hold out a begging bowl. People seem to be less aprehensive if you call them something other then what they really are. I like to refer to them as donations or alms. Also learn to ask for",0,20,4);
            base.books.AddEnglishBookText("money at the right momment. Chances are best if you ask when the target is happy, often visable through the indication of a smile or audible laughter. Avoid asking when someone is being stabbed, it's ",0,20,4);
            base.books.AddEnglishBookText(" discourtious to do so until after the stabbing has subsided.",0,20,4);
            base.books.AddEnglishBookText("And that's the thick and thin of becoming a simple monk, all that is left is to put this book down and set it on fire, I make ten copper every time they need to replace a copy",0,20,4);
            
            base.books.AddGermanBookText("Seien Sie ein Mönch\n\nVon Logrin Parr",0,20,4);
            base.books.AddGermanBookText("Inhaltsverzeichnis\n\nWidmungen\nKapitel eins: Was ist ein Mönch?\nKapitel zwei: Beachte die Rolle\nKapitel drei: Pflichten eines Mönchs ",0,20,4);
            base.books.AddGermanBookText("Widmungen\n\nIch widme dieses Buch den Damen. ",0,20,4);
            base.books.AddGermanBookText("Kapitel eins - Was ist ein Mönch?\n\nViele Leute fragen mich nach meiner Berufung, oder sagen mir ich sollte mir eine Hose anziehen. Auf beide Anreden antworte ich, dass ich nur ein einfacher Mönch",0,20,4);
            base.books.AddGermanBookText("bin. Weil ich ebendies wahrhaft bin. An diesem Punkt solltet Ihr Euch wahrscheinlich fragen, wie es kommt dass ein blinder Mann ein Buch geschrieben hat das Ihr in Euren Händen haltet, oder zunächst ",0,20,4);
            base.books.AddGermanBookText("einmal warum ich keine Hose tragen sollte. Am Besten verweile ich nicht bei solchen Dingen.\n\nAlso fragt Ihr Euch inzwischen, was ist ein Mönch? Oder Ihr habt das Buch weggelegt und seid",0,20,4);
            base.books.AddGermanBookText("hinausgegangen, weil dort die Sonne scheint. Trotz der geringen Wahrscheinlichkeit dass Ihr noch immer lest, werde ich nun erklären was ein Mönch ist\n\nGroßartig ",0,20,4);
            base.books.AddGermanBookText("Es mag maßlos erscheinen, aber nachdem ich eine kurze Liste der Dinge angefertigt habe die mir in der letzten Zwergenwoche widerfahren sind, wurde mir klar dass kein anderes Wort es so eindeutig",0,20,4);
            base.books.AddGermanBookText("zusammenfasst. Zu Euren Gunsten habe ich diese Liste hier eingefügt: \n\n1. Musste nicht eine Kupfermünze in der Taverne lassen, um mich derart zu betrinken dass ich nicht mehr verständlich sprechen",0,20,4);
            base.books.AddGermanBookText("konnte.\n2. Rettete einen Laden indem ich ein aufflackerndes Inferno m3.\n3. Setzte einen Laden in Flammen (noch vor Nummer 2)\n4. Brachte einen Ork für eine neue Tasche zum Singen",0,20,4);
            base.books.AddGermanBookText("5. Gab besagtem Ork die Tasche dann nicht\n6. Warf eine Tasche ins Feuer weil ich zu viele besaß.\n7. Ging Untote angeln\n8. Griff einen massigen schwarzen Vogel an, indem ich Sterne nach ihm warf als",0,20,4);
            base.books.AddGermanBookText("er seine Pfeife rauchte.\n9. Brachte eine aufgebrachte Echsenfrau dazu, Lampen nach mir zu werfen.\n10. Schrieb ein Buch obwohl ich blind bin.",0,20,4);
            base.books.AddGermanBookText("Jetzt nehmt Euren Zeigefinger und deutet zufällig auf eine der oben erwähnten Betätigungen. Mit einer Wahrscheinlichkeit von 7 zu 10 war ich währenddessen nakt.\n\nAn dieser Stelle bin ich sicher Ihr",0,20,4);
            base.books.AddGermanBookText("stimmt mir zu dass ich ehrfurchtgebietend und ein Gott unter den Männern bin, also ohrfeigt Euch selber für eine solche Blasphemie. Ich meine das toternst, macht schon, das Buch wird noch immer hier",0,20,4);
            base.books.AddGermanBookText("sein wenn Ihr das erledigt habt. Gut. Nun, nachdem das getan ist wollt Ihr sicher ebenso fantastisch sein wie ich es bin. Um das gewünschte Resultat leichter zu erreichen, lest den Rest des Buches",0,20,4);
            base.books.AddGermanBookText("nakt",0,20,4);
            base.books.AddGermanBookText("Kapitel zwei - Beachte die Rolle\n\nNun, jetzt da Ihr nakt seid ist der perfekte Anfangspunkt, ein einfacher Mönch zu werden. Schliesslich, wenn Ihr nicht wie ein Mönch ausseht dann werden die Leute",0,20,4);
            base.books.AddGermanBookText("denken Ihr seid irgendeine Art Verrückter Bengel weil Ihr einem Troll ins Gesicht schlagt.\n\nDas Wichtigste das Ihr Euch merken müsst ist es, keine Robe zu tragen. Priester tragen Roben, und Ihr ",0,20,4);
            base.books.AddGermanBookText("wollt sicherlich nicht wie ein Priester aussehen. Immerhin ist das Einzige, was einen Priester von einem völlig Irren an der Strassenecke unterscheidet, seine Robe. Also sofern Ihr nicht wünscht,",0,20,4);
            base.books.AddGermanBookText("unintelligent herumzubrabbeln und Kühe an Leute zu verleihen, vermeidet Roben um jeden Preis.\n\nEs gibt zwei Möglichkeiten sich wie ein Mönch zu kleiden, es kommt letztlich nur auf Eure persönlichen ",0,20,4);
            base.books.AddGermanBookText("Vorlieben an, welchen Ihr wählt weil er der Beste für Euch ist. Ich habe sie der Einfachheit halber getrennt.\n\nErste Methode: Nehmt drei Ausstaffierungen die sich durch ihren Stil stark",0,20,4);
            base.books.AddGermanBookText("unterscheiden. Zum Beispiel die Lederkleidung eines Waldläufers, das seidene Gewand eines Königs und eine Rüstung. Nehmt die benötigten Materialien und lasst einen Ork mit einem Hammer darauf ",0,20,4);
            base.books.AddGermanBookText("einschlagen. Sammelt die grössten Überreste auf und beginnt sie zufällig zusammenzustückeln. Die zuvorgenannten Kleidungsstücke könnten kombiniert die Seidenrüstung eines Waldläufers genannt werden.",0,20,4);
            base.books.AddGermanBookText("Und ebenso ein Schandfleck. Keine Bange, dies ist der Stil den Ihr anstrebt.\n\nZweite Methode: Bleibt einfach nakt und verlasst die Bücherei. ",0,20,4);
            base.books.AddGermanBookText("Kapitel drei - Pflichten eines Mönchs\n\nEin Mönch zu sein bedeutet nicht nur Spiel und Spass, die Leute erwarten gewisse Dinge von Euch. Ihr wollt Eurer geliebtes Publikum doch nicht enttäuschen,",0,20,4);
            base.books.AddGermanBookText("oder? Daher ist es in der Tat nötig, sich an einige Prinzipien zu halten.\n\n1. Sympatisch sein ",0,20,4);
            base.books.AddGermanBookText("Denkt daran dass der Großteil Eures Einkommens nach Euch geworfen wird wenn Ihr an Stadtbewohnern vorbeilauft, selbst wenn Ihr jemanden und seine Art, die Schultern zu zucken und sein Halstuch",0,20,4);
            base.books.AddGermanBookText("zurechtzurücken verabscheut, gluckst und bleibt ruhig. Ausserdem, wenn Ihr zu wirklich jedem freundlich seid, bietet dies einen hübschen Vorwand um Eure Hände \"aus Versehen\" wandern zu lassen wenn Ihr",0,20,4);
            base.books.AddGermanBookText("einen besonders gut ausgestatteten Ritter umarmt. Der freundliche alte Herr Parr würde sowas natürlich nie tun, ist nur eine kleine Anmerkung, meine Damen.\n\n2. Glucksen lernen\nMönche lachen nicht, ",0,20,4);
            base.books.AddGermanBookText("wir lachen nicht schallend, nicht leise, nicht wiehernd und nicht kichernd. Mönche glucksen. Dies wurde vor so langer Zeit in Stein gemeisselt dass selbst die ersten Mönche die Herkunft dessen nicht",0,20,4);
            base.books.AddGermanBookText("mehr kennen. Falls Ihr feststellt dass Ihr dieses Gebot übertreten habt müsst Ihr eine Hand voll Asche essen. Ich denke mir das auch nicht aus nur um Menschen dazu zu bringen Asche zu essen.",0,20,4);
            base.books.AddGermanBookText("3. Eine tragische Geschichte erzählen können\nMein Leben war ein unausweichlicher Strudel die Seele zerfetzenden Schmerzes, daher hatte ich es leicht. Falls Ihr keine traurige Geschichte erzählen",0,20,4);
            base.books.AddGermanBookText("könnt, werdet Ihr eine Menge Einkünfte einbüssen. Oder würdet Ihr Euer schwer verdientes Geld jemandem geben der erzählt dass er mit einem goldenen Löffel im Mund geboren wurde und in einem blühenden ",0,20,4);
            base.books.AddGermanBookText("Obstgarten aufgewachsen ist? Um dieses ungemütliche Szenario zu vermeiden, verletzt Euch wann immer jemand nach eurer Vergangenheit fragt, und erklärt dann dass Ihr nicht darüber reden wollt. Die",0,20,4);
            base.books.AddGermanBookText(" meisten Leute werden davon ausgehen dass Ihr es schwer hattet.\n\n4. Ein Experte sein\nDie Leute erwarten von einem Mönch, dass er ein Mann von Welt ist, also wenn Euch jemand eine Frage stellt und ",0,20,4);
            base.books.AddGermanBookText("Ihr wisst keine Antwort, dann habt Ihr die Hoffnungen und Träume desjenigen zerschmettert wie ein Ork, der versucht Glas zu blasen. Keine Bange, merkt Euch einfach diese leichte Regel; Wenn sie fragen",0,20,4);
            base.books.AddGermanBookText("wissen sie es selber nicht. Daher erfindet einfach etwas, sie werden den Unterschied nicht einmal bemerken. Denkt daran dass ein Zuhörer Euch verbessern könnte, daher nehmt die Person zur Seite und",0,20,4);
            base.books.AddGermanBookText("flüstert Euer Hirngespinst mit unterdrückter Stimme. Sie werden sich fühlen als wenn Ihr ihnen ein grosses Geheimnis anvertraut und sich wichtig vorkommen. Und glückliche Leute sind allgemeinhin",0,20,4);
            base.books.AddGermanBookText("großzügig wenn man sie anbettelt.\n\n5. Weise sein\nWeisheit ist etwas das ein Mönch ausstrahlen sollte. Um dies zu tun, braucht Ihr drei Dinge;\n1. Einen Zuhörer für Eure Weisheit\n2. Eine Pfeife",0,20,4);
            base.books.AddGermanBookText("3. Sibanc\nMit diesen drei Dingen wird die Weisheit von selbst kommen.\n\n6. Einen Vertrauten beschaffen",0,20,4);
            base.books.AddGermanBookText("6. Einen Vertrauten beschaffen\n\nEinen exotischen Vertrauten mit Euch herumzutragen wird Euch mysteriös und nicht von dieser Welt erscheinen lassen, besonders wenn das Tier von einzigartiger Farbe",0,20,4);
            base.books.AddGermanBookText("ist. Sollte das Tier nicht von Natur aus anders sein als seine Artangehörigen, färbt es wie Ihr wollt. Seid nicht um die Natur besorgt, Ihr seid ein Mönch, kein Druide.",0,20,4);
            base.books.AddGermanBookText("Beispiele für gute Begleiter: Vögel, Wölfe, Schlangen, Bären, Alles was nur noch ein Auge hat, Schildkröten\nBeispiele für schlechte Begleiter: Wanzen, Schweine, Kühe, Leblose Dinge, Insektenschwärme,",0,20,4);
            base.books.AddGermanBookText("Schildkröten\n\nSchildkröten sind doppelt aufgelistet da es auf die einzelne Schildkröte ankommt, manchmal haben sie schlechte Angewohnheiten\n\n7. Betteln ",0,20,4);
            base.books.AddGermanBookText("Das Betteln zu erlernen ist eine Grundvoraussetzung, wenn man als Mönch überleben will. Bittet einfach um Geld und haltet ein Bettelbehältnis hin. Die Menschen scheinen weniger besorgt zu sein wenn ",0,20,4);
            base.books.AddGermanBookText("man es etwas anderes nennt als es ist. Ich mage es, diese Gelder Spenden oder Almosen zu nennen. Lernt auch, im richtigen Augenblick nach Geld zu fragen. Die besten Erfolgschancen hat man wenn die",0,20,4);
            base.books.AddGermanBookText("Zielperson glücklich ist, was man oft durch ein sicheres Indiz: Lächeln oder sogar hörbares Lachen, feststellen kann. Vermeidet zu betteln während jemand erstochen wird, es ist unhöflich dies zu tun",0,20,4);
            base.books.AddGermanBookText("bis zu dem Zeitpunkt wenn die Erstechung vollzogen wurde.\n\nUnd das ist im Grossen und Ganzen der Weg, ein einfacher Mönch zu werden. Was Euch jetzt noch zu tun bleibt ist es das Buch wegzulegen und ",0,20,4);
            base.books.AddGermanBookText("anzuzünden, ich erhalte zehn Kupferstücke jedes Mal wenn sie eine neue Ausgabe davon benötigen.",0,20,4);
           
            --------------------------------------

            base.books.AddLanguage("common language",5);

            base.books.AddEnglishBookText("\n\n~~Diamonds~~",285,0,5);
            base.books.AddEnglishBookText("Diamonds are among the rarest gems on this island. Their clarity is often compared with the one of a clever mind, which is why these gems are often used by followers of Eldan and savants.",0,0,5);
            base.books.AddEnglishBookText("Magic diamonds have often developed their natural resistance and are able to transmit these energies onto other objects.",0,0,5);
			base.books.AddEnglishBookText("Weapons:\n\nIf a magic diamond transmits it's energies onto a weapon, this enhances it's durability. Weapons don't degrade as fast as usual and tends to break less often.",3035,0,5);
            base.books.AddEnglishBookText("Armors and Shields:\n\nThe same effect here: The durability of the object is enhanced. It can, like weapons, reach on to indestructability.",2369,0,5);
            base.books.AddEnglishBookText("Magic wands:\n\nEven magic wands can be made more endurable with the aid of magic energies.",57,0,5);
			base.books.AddEnglishBookText("Tools:\n\nMagic energies also enhance the durability of tools. Do not confuse them with the energies of emeralds, which tend to enhance the durability of the crafted items.",551,0,5);
            base.books.AddEnglishBookText("Trinkets:\n\nIf one lets the magic energies flow into trinkets, cleverness can be enhanced.",222,0,5);
            base.books.AddEnglishBookText("There is one important thing to note: One should never let the energies of a diamond flow into a trinket in which there is another stone embedded.",0,0,5);

            base.books.AddGermanBookText("\n\n~~Diamanten~~",285,0,5);
            base.books.AddGermanBookText("Diamanten gehören mit zu den seltensten Edelsteinen auf dieser Insel. Ihre Klarheit wird oft mit der eines klugen Geistes verglichen, weswegen diese Edelsteine von vielen Eldan-Anhängern und Gelehrten getragen werden.",0,0,5);
            base.books.AddGermanBookText("Magische Diamanten haben oft ihre natürliche Wiederstandskraft weiterentwickelt und sind in der Lage diese Energien auf andere Gegenstände zu übertragen.",0,0,5);
			base.books.AddGermanBookText("Waffen:\n\n Überträgt ein magischer Diamant seine Energien auf eine Waffe so verlängert man dadurch die Haltbarkeit. Die Waffen nutzen sich nicht mehr so rasch ab und brechen seltener.",3035,0,5);
            base.books.AddGermanBookText("Rüstungen und Schilde:\n\nAuch hier verlängert die Energie eines magischen Diamanten die Lebensdauer. Dies kann, wie bei den Waffen auch, bis hin zur Unzerstörbarkeit reichen.",2369,0,5);
            base.books.AddGermanBookText("Magierstäbe:\n\nSelbst die Stäbe der Magier können durch das Übertragen der magischen Energien haltbarer gemacht werden.",57,0,5);
            base.books.AddGermanBookText("Werkzeuge:\n\nMagische Energien verlängern ebenso die Haltbarkeit von Werkzeugen. Nicht zu verwechseln mit den Energien des Smaragdes, die die Haltbarkeit der gefertigten Gegenstände verlängern.",551,0,5);
            base.books.AddGermanBookText("Schmuckstücke:\n\nLässt man die Energien eines magischen Diamantes in ein Schmuckstück fließen, so hilft er dem Träger zu einem klügerem Geist.",222,0,5);
            base.books.AddGermanBookText("Zu beachten ist hierbei, dass man niemals die Energien eines Diamanten in ein Schmuckstück fließen lassen sollte, in welches ein andersartiger Stein eingefasst wurde.",0,0,5);
			--------------------------------------------------------

            base.books.AddLanguage("halfling language",6);
            
            
			base.books.AddEnglishBookText("The book of the jokes",0,7,6); 
			base.books.AddEnglishBookText("",0,7,6); 
			base.books.AddEnglishBookText("1. \nA Salkamaerian, an Albarian and a Gynkeesh go into a tavern.\nWhat did they do at the end of the evening?",0,7,6); 
			base.books.AddEnglishBookText("The Salkamaerian got drunk and gave the guests a lesson about manners.\nThe Albarian got drunk and insulted the guests.\n",0,7,6); 
			base.books.AddEnglishBookText("The Gynkeesh sold two drunken slaves.",0,7,6); 
			base.books.AddEnglishBookText("2.\nAn innkeeper´s last words\n\n\n\n\"No beer for you and your dwarven friend any more.\"",0,7,6); 
			base.books.AddEnglishBookText("3.\nWhat does an elf staring at a blank paper?\n\n\n\nHe studies his rights in Greenbriar.",0,7,6); 
			base.books.AddEnglishBookText("4.\nan elf is complainig to a halfling:\nelf: these rolls are from yesterday. i want rolls from today!",0,7,6); 
			base.books.AddEnglishBookText("the halfling looks incomprehending at the elf.\nhalfling: so why don't you come back tomorrow?",0,7,6); 
			base.books.AddEnglishBookText("5.\nHow should you never address a dwarven king?\n\n\n\n\nYour Highness...",0,7,6); 
			base.books.AddEnglishBookText("6.\nElf: I just noticed that dwarfs only use one third of their brain.\nDwarf: Ah? And what do they do with the other half?",0,7,6); 
			base.books.AddEnglishBookText("7.\nat the harbour a dead dwarf is found in the river. bound up, gagged, wound up. bound up, gagged, wound up in an in an iron chain, and with a heavy stone bound to his feet. ",0,7,6); 
			base.books.AddEnglishBookText("the guard investigates. laconic comment by the corporal: \"shitty dwarves, allways steal more, than they can carry...\"",0,7,6); 
			base.books.AddEnglishBookText("8.\nA royal guard's last words\n\n\n\nNo entry",0,7,6); 
			base.books.AddEnglishBookText("9.\nIn preparation for a fight a knight of the Grey Rose looks for a sword because he has lost his. He shouts: \"Men! A sword!\"",0,7,6); 
			base.books.AddEnglishBookText("There is clash and clangor.\nThen the knight says with pain: Men! A healer!\"",0,7,6); 
			base.books.AddEnglishBookText("10.\nFooser´s last words\n\n\n\n\"Pshaw! They are only peasants!\"",0,7,6); 
			base.books.AddEnglishBookText("11.\nHow many elves does it take to light a candle?\n\n\n\n\nNon, when they finally decided to do it it's already bright outside.",0,7,6); 
			base.books.AddEnglishBookText("12.\nWhy does a Norodaj have a camel?\n\n\n\n\nSomeone told him it's a desert ship.",0,7,6); 
			base.books.AddEnglishBookText("13.\nOne day before the election to the new governour of Trolls Bane: An old woman slips and falls onto her back. Desperately she tries to stand up again. Just in that moment Edward Cromwell comes",0,7,6); 
			base.books.AddEnglishBookText("along and helps the old woman up on her feet and she says thanks. \"You for sure know already who you have to vote in the next election?",0,7,6); 
			base.books.AddEnglishBookText("Can I count on your vote?\" Answers the old woman: \"I'm sorry, Mister Cromwell. I fell on my back - not on my head.\"",0,7,6); 
			base.books.AddEnglishBookText("14.\nhow do you know you are passing through elven inhabitaed lands?\n\n\n\n\nthe grass is well grazed...",0,7,6); 
			base.books.AddEnglishBookText("15\nhow to bring a dwarf to jump up and down continuosly? \n\n\n\nnail a goldpiece under the ceiling.",0,7,6); 
			base.books.AddEnglishBookText("16.\nDwarf to elf: \"Did you know that only carnivorous creatures are able to evolve intelligence?\"\nElf: \"What do you mean?\"",0,7,6); 
			base.books.AddEnglishBookText("Dwarf: \"Well, what do you think what IQ one needs to stalk a blade of grass?\"",0,7,6); 
			base.books.AddEnglishBookText("17\nStephen Rothman gives a real big \"castle-warming\" party. Everybody is eating and feasting. during the meal Rothman anounces screaming: \"tomorrow we will kill 1000 dwarves - and a Gnome.",0,7,6); 
			base.books.AddEnglishBookText("everybody looks up, and Fooser asks: \"an why please a Gnome?\"\nsays Rothman to Arameh: \"you see? nobody asks for the 1000 dwarves...\"",0,7,6); 
			base.books.AddEnglishBookText("18.\nWhy doesn't Stephen Rothman hit children?\n\n\n\n\nThey might be his own.",0,7,6); 
			base.books.AddEnglishBookText("19.\nwhy does Stephen Rothman never plays hide and seek?\n\n\n\n\nbecause nobody is looking for him...",0,7,6); 
			base.books.AddEnglishBookText("20.\nHow many goblins does it take to brew an explosive potion?\n\n\n\n\nDepends on the number of tries.",0,7,6); 
			base.books.AddEnglishBookText("21.\nAn elf meets a dwarf. \nDwarf: \"Looking at you, one might think there is a famine.\" ",0,7,6); 
			base.books.AddEnglishBookText("Elf: \"And looking at you, one might think you´re responsible for it.\"",0,7,6); 
			base.books.AddEnglishBookText("22.\nWhich one is more difficult to build: a snow-elf or a snow-dwarf?\n\n\n\n\nOf course a snow-dwarf because one has to hollow the head!",0,7,6); 
			base.books.AddEnglishBookText("23.\nEdward Cromwell gets kidnapped. The kidnappers demand 10 gold, or else they will release him.",0,7,6); 
			base.books.AddEnglishBookText("24.\nthree Krokodiles meet.\nsays the first: four weeks ago i ate a dwarf. he had so much beer inside, i still feel drunk.",0,7,6); 
			base.books.AddEnglishBookText("says the second: i ate an ork, two weeks ago. he was so full of garlick, that even now my stomach acid is still rising.",0,7,6); 
			base.books.AddEnglishBookText("says the third: thats nothing. three weeks ago i ate a Kallahorn. that one was so hollow, that i can't dive anymore.",0,7,6); 
			base.books.AddEnglishBookText("25.\nIn Gobaith they ask all women, if they would go to bed with Stephen.\n2 % said: \"yes\" 3 % said: \"no\" and 95% said: \"never again!\"",0,7,6); 
			base.books.AddEnglishBookText("26.\nA man enters a tavern:\n\"hello folks, do you know the newest Kallahornjoke? it goes: ...\" Before he can tell more, a Type like a bear stands up, and says with menacing voice:",0,7,6); 
			base.books.AddEnglishBookText("\"see... i am the swordmaster of Kallahorn, the tough looking guy over there is the best archer of the island, beside him sit the ex governer and arameh ... do you still want to tell a Kallahornjoke?!\"",0,7,6); 
			base.books.AddEnglishBookText("\"well, really, i am not so keen anymore\", says the newcomer, \"would be a bit boring, to explain the same joke five times...\"",0,7,6); 
			base.books.AddEnglishBookText("27.\nA lizard, an elf and a dwarf go to a tavern and each orders a dish of soup. But there is a fly in each one.\nThe lizard briefly glances at the fly, snatches it with his tongue and eats it.",0,7,6); 
			base.books.AddEnglishBookText("The elf carefully takes the fly out of the soup, pets it endearingly and asks: \"Are you alive?\"",0,7,6); 
			base.books.AddEnglishBookText("The dwarf throws the fly on the table and shouts: \"Spit out immediately my soup!\"",0,7,6); 
			base.books.AddEnglishBookText("28.\nA dwarf carrying muck in his hands enters a tavern and shouts proudly: \"Hey guys, look into what I´ve almost stepped\"",0,7,6); 
			base.books.AddEnglishBookText("29.\nA dwarf askes at a rowing barge, \"how much is the fare to silverbranbd?\" \"two goldcoins.\" the captain says. aghast the dwarf turns back. ",0,7,6); 
			base.books.AddEnglishBookText("as the barge starts the journey downriver, the dwarf",0,7,6); 
			base.books.AddEnglishBookText("as the barge moores the next time, the dwarf comes nearer, tongue hanging out. \"how much ist the passage to silverbrand from here?\"",0,7,6); 
			base.books.AddEnglishBookText("the captain grinns \"four gold coin. - silverbrand is upriver....\"",0,7,6); 
			base.books.AddEnglishBookText("30.\nHow many elves do you need to kill a cow?\n\n\n\n\n10! 9 elves lift the cow up and the 10th eats all the grass.",0,7,6); 
			base.books.AddEnglishBookText("31.\nHow to change a dwarf into an elf?\n\n\n\n\nshave, wash, stretchbench...",0,7,6); 
			base.books.AddEnglishBookText("32.\nWhy would a wolf eat apples?\n\n\n\n\nHe has just bitten an Orc one hour ago and needs to get rid of the taste from his mouth.",0,7,6); 
			base.books.AddEnglishBookText("33.\nA dwarf, an elf and a goblin are captured by an ogre tribe. The ogres ask the goblin for his last wish. \"Yeah, hunger...meet!\" The goblin gets an old joint and eats all the meet.",0,7,6); 
			base.books.AddEnglishBookText("Then the ogres bash his head, eats him during an orgy and covers a drum with his skin.\nThe ogres ask the elf for his last wish.\" I want to sing a last song\" The elf gets his harp and sings",0,7,6); 
			base.books.AddEnglishBookText("a drippy and poetic song. Then the ogres bash his head, eats him during a one-day-orgy and covers a drum with his skin.",0,7,6); 
			base.books.AddEnglishBookText("The ogres ask the dwarf for his last wish. \"I woud like to have a knife a last time.\"",0,7,6); 
			base.books.AddEnglishBookText("The ogres consult quickly and decide that he is no danger for them.",0,7,6); 
			base.books.AddEnglishBookText("He gets the knife, grabs it and stabs five times into his stomach and yells: \"You won´t make a drum out of me!\"",0,7,6); 
			base.books.AddEnglishBookText("End",0,7,6);

			base.books.AddGermanBookText("Das Buch der Witze",0,7,6); 
			base.books.AddGermanBookText("",0,7,6); 
			base.books.AddGermanBookText("1. \nEin Salkamaerianer, ein Albarianer und ein Gynkeesh gehen in eine Taverne.\nWas haben sie am Ende des Abends getan?\n ",0,7,6); 
			base.books.AddGermanBookText("Der Salkamaerianer hat sich betrunken und den Gästen Lektionen über gutes Benehmen erteilt.\nDer Albarianer hat sich betrunken und die Gäste beleidigt.",0,7,6); 
			base.books.AddGermanBookText("Der Gynkeesh hat zwei betrunkene Sklaven verkauft.",0,7,6); 
			base.books.AddGermanBookText("2.\nDie letzten Worte eines Wirts\n\n\n\n\"Kein Bier mehr für dich und deinen Zwergenfreund.\"",0,7,6); 
			base.books.AddGermanBookText("3.\nWas tut ein Elf, der auf ein leeres Blatt Papier starrt?\n\n\n\nEr studiert seine Rechte in Greenbriar.",0,7,6); 
			base.books.AddGermanBookText("4.\nEin Elf beschwert sich bei einem Halbling:\nElf: Diese Brötchen sind von gestern, ich will welche von heute!",0,7,6); 
			base.books.AddGermanBookText("Der Halbling schaut den Elf dumm an.\nHalbling: Nun, dann kommen sie doch morgen wieder!",0,7,6); 
			base.books.AddGermanBookText("5.\nWie sollte man den Zwergenkönig niemals anreden?\n\n\n\n\nEure Hoheit...",0,7,6); 
			base.books.AddGermanBookText("6.\nElf: \"Ich habe festgestellt, dass Zwerge nur ein Drittel ihres Gehirns gebrauchen...\"\nZwerg: \"Ach, und was machen sie mit der anderen Hälfte?\"",0,7,6); 
			base.books.AddGermanBookText("7.\nAm Hafen wird ein toter Zwerg aus dem Fluss gezogen. Gefesselt, geknebelt, mit einer Eisenkette umwickelt und mit einem schweren Stein an den Füßen. ",0,7,6); 
			base.books.AddGermanBookText("Die Stadtwache untersucht den Zwerg und der Korporal meint ungerührt: \"Mistzwerge, klauen immer mehr als sie tragen können.\"",0,7,6); 
			base.books.AddGermanBookText("8.\nDie letzten Worte einer Stadtwache.\n\n\n\nKein Durchgang",0,7,6); 
			base.books.AddGermanBookText("9.\nKurz vor einem Kampf will ein Ritter der Grauen Rose ein Schwert organisieren, weil er seins verloren hat, und ruft:\"Männer!!! Ein Schwert!!!\"",0,7,6); 
			base.books.AddGermanBookText("Man hört Geschäpper und Geklirre!!! \nDann sagt der Ritter vom Schmerz erfüllt:\"Männer!!! Einen Heiler!!!\"",0,7,6); 
			base.books.AddGermanBookText("10.\nDie letzten Worte von Fooser\n\n\n\n\"Pah, das sind doch nur Bauern!\"",0,7,6); 
			base.books.AddGermanBookText("11.\nWie viele Elfen braucht es um eine Kerze anzuzünden?\n\n\n\n\n",0,7,6); 
			base.books.AddGermanBookText("12.\nWas macht ein Norodaj mit einem Kamel?\n\n\n\n\nJemand sagte ihm das sei ein Wüstenschiff.",0,7,6); 
			base.books.AddGermanBookText("13.\nEinen Tag vor der Wahl zum neuen Governour von Trolls Bane: Eine alte Frau stürzt und fällt auf den Rücken. Verzweifelt versucht sie wieder aufzustehen. Zufällig kommt Edward Cromwell vorbei und ",0,7,6); 
			base.books.AddGermanBookText("hilft der alten Dame auf und sie bedankt sich. \"Sie wissen ja wohl für wen sie bei der nächsten Wahl stimmen sollen? Kann ich mit ihrer Stimme rechnen?\" Antwortet die",0,7,6); 
			base.books.AddGermanBookText("alte Frau: \"Tut mir leid, Mister Cromwell. Ich bin auf den Rücken gefallen - nicht auf den Kopf.\"",0,7,6); 
			base.books.AddGermanBookText("14.\nWoran erkennt man, das die Gegend die man gerade durchwandert von Elfen bewohnt ist?\n\n\n\n\nDas Gras ist so kurz abgefressen.",0,7,6); 
			base.books.AddGermanBookText("15.\nWie bringt man einen Zwerg in der Taverne zum hüpfen?\n\n\n\n\nIn dem man ein Goldstück an die Decke nagelt.",0,7,6); 
			base.books.AddGermanBookText("16.\nZwerg zum Elf: \"Wusstest du schon, daß nur Fleischfresser Intelligenz entwickeln können?\"\nDarauf der Elf: \"Wie meinst du denn das?\"",0,7,6); 
			base.books.AddGermanBookText("Zwerg: \"Na wieviel IQ braucht man schon um sich an einen Grashalm anzupirschen?\"",0,7,6); 
			base.books.AddGermanBookText("17.\nStephen Rothman hält zur Einweihung der FEstung Kallahorn ein riesiges Bankett. Alles Essen und es wird gefeiert.\nMitten im Essen brüllt Rothman: \"Morgen töten wir 1000 Zwerge - und einen Gnom.\"",0,7,6); 
			base.books.AddGermanBookText("Alle blicken auf und Fooser fragt: \"Wieso denn bitteschön einen Gnom?\"\nDa meint Rothman zu Arameh: \"Siehste? Nach den 1000 Zwergen fragt kein Mensch...\"",0,7,6); 
			base.books.AddGermanBookText("18\nWarum schlägt Stephen Rothman keine Kinder?\n\n\n\n\nKönnten ja seine sein.",0,7,6); 
			base.books.AddGermanBookText("19\nWarum spielt Stephan Rothman nie verstecken?\n\n\n\n\n",0,7,6); 
			base.books.AddGermanBookText("20.\nWieviele Goblins braucht es um einen explodierenden Trank zu brauen?\n\n\n\n\nDas kommt auf die Anzahl der Versuche an.",0,7,6); 
			base.books.AddGermanBookText("21.\nBegegnen sich ein Elf und ein Zwerg.\nDer Zwerg: \"Also wenn man dich so sieht, könnte man meinen bei euch wäre eine Hungersnot ausgebrochen.",0,7,6); 
			base.books.AddGermanBookText("Darauf der Elf: \"und wenn man dich so sieht, könntest du dafür verantwortlich sein!\"",0,7,6); 
			base.books.AddGermanBookText("22.\nWas ist schwerer, einen Elfenschneeman oder einen Zwergenschneeman zu bauen?\n\n\n\n\nDer Zwergenschneemann natürlich, da muß man erst den Kopf aushölen!",0,7,6); 
			base.books.AddGermanBookText("23.\nEdward Cromwell wird entführt. Die Entführer verlangen 10 Gold, ansonsten lassen sie ihn frei.",0,7,6); 
			base.books.AddGermanBookText("24.\nTreffen sich drei Krokodile.\nSagt das Erste: Ich habe vor vier wochen einen Zwergen gefressen. Der hatte so viel Bier getrunken, ich bin immer noch voll.\nSagt das Zweite: Ich hab vor zwei",0,7,6); 
			base.books.AddGermanBookText("Wochen einen Ork gefressen. Der hatte so viel Knoblauch intus, mir stößt es jetzt noch den Magen hoch.\nSagt das Dritte: Das ist doch noch gar nicht. Ich hatte vor drei Wochen",0,7,6); 
			base.books.AddGermanBookText("einen Kallahorn gefressen. Der war sooo hohl, dass ich jetzt noch nicht tauchen kann.",0,7,6); 
			base.books.AddGermanBookText("25.\nIn Gobaith werden alle Frauen befragt, ob die mit Stephen Rothman ins Bett gehen würden.\n2% sagten: \"ja\" 3% sagten: \"nein\" und 95% sagten: \"nie wieder!\"",0,7,6); 
			base.books.AddGermanBookText("26.\nEin Mann kommt in eine Kneipe: \"Hallo Leute, kennt ihr schon den neuesten Kallahornwitz? Also: ...\"\nBevor er weitererzählen kann, erhebt sich ein bärenhafter Typ und sagt mit drohender Stimme: ",0,7,6); 
			base.books.AddGermanBookText("\"Also... ich bin der Schwertmeister der Kallahorns, der drahtige Typ da am Tisch ist der Beste Bogenschütze der Insel, neben ihm sitzen der ehemalige Goveneur und Arameh ... wollen Sie noch",0,7,6); 
			base.books.AddGermanBookText("immer einen Kallahornrwitz erzählen?!\"\n\"Eigentlich ist mir die Lust vergangen\", sagt der Neuankömmling, \"fünfmal denselben Witz erklären ist fad...\"",0,7,6); 
			base.books.AddGermanBookText("27.\nEin Echsenmensch, ein Elf und ein Zwerg kommen in eine Taverne und bestellen sich jeweils einen Teller Suppe. Doch in jeder Suppe ist eine Fliege.\nDer Echsenmensch schaut sich die Fliege",0,7,6); 
			base.books.AddGermanBookText("kurz an, läßt seine Zunge aus dem Maul schnellen und frißt sie.\nDer Elf holt sie vorsichtig aus der Suppe heraus, streichelt sie zärtlich und fragt: \"Lebst du noch?\"",0,7,6); 
			base.books.AddGermanBookText("Und der Zwerg schleudert sie auf den Tisch und schreit: \"Spuck sofort meine Suppe wieder aus!!!\"",0,7,6); 
			base.books.AddGermanBookText("28.\nKommt ein Zwerg in eine Taverne, trägt Mist in seinen Händen und ruft: \"Hey Leute, seht doch mal wo ich beinahe hineingetreten wäre!\"",0,7,6); 
			base.books.AddGermanBookText("29.\nEin Zwerg erkundigt sich bei einer Ruderbarke am großen Fluß: \"Wieviel kostet die Fahrt nach Silberbrand?\" \"Zwei Goldmünzen!\" meint der Kapitän. Entsetz dreht sich der Zwerg um.",0,7,6); 
			base.books.AddGermanBookText("Als die Barke ablegt und Flußabwärts gleitet, rennt der Zwerg am Ufer neben her. Die ganze Fahrt lachen der Kapitän und die Ruderer, aber dem kurzbeinigem Geizhals geht die Puste nicht aus.",0,7,6); 
			base.books.AddGermanBookText("Als die Barke das nächste Mal anlegt, keucht der Zwerg heran. \"Wieviel - japps - Wieviel kostet die Fahrt nach Silberbrand von hier?\"",0,7,6); 
			base.books.AddGermanBookText("\"Jetzt kostets vier Goldmünzen.\" Johlt der Kaptän. \"Silberbrand liegt nämlich stromaufwärts.\"",0,7,6); 
			base.books.AddGermanBookText("30.\nWieviel Elfen braucht man um eine Kuh zu töten?\n\n\n\n\n10! 9 Elfen heben die Kuh hoch und der 10. frisst ihr das Gras weg.",0,7,6); 
			base.books.AddGermanBookText("31.\nWie verwandelt man einen Zwerg in einen Elfen?\n\n\n\n\nWaschen, rasieren, Streckbank ...",0,7,6); 
			base.books.AddGermanBookText("32.\nWarum frißt ein Wolf plötzlich Äpfel?\n\n\n\n\nEr hat vor einer Stunde einen Ork gebissen und kriegt den Geschmack nicht mehr aus dem Maul.",0,7,6); 
			base.books.AddGermanBookText("33.\nEin Zwerg, ein Elf und ein Goblin werden von einem Ogerstamm gefangen. Die Oger fragen den Goblin, ob er noch einen letzten Wunsch hat. \"Ja, Hunger...Fleisch!!\" Der Goblin bekommt eine alte Keule",0,7,6); 
			base.books.AddGermanBookText("mit einigen Fleischresten und nagt sie ab. Dann schlagen ihm die Oger den Schädel ein, fressen ihn in einem Gelage auf und bespannen mit seiner Haut eine Trommel.",0,7,6); 
			base.books.AddGermanBookText("Die Oger fragen den Elf, ob er noch einen letzten Wunsch hat. \"Ja, ich will noch mal ein Lied singen!\" Der Elf ergreift seine Harfe und singt sein Lied voll süßem Schmelz und zarter Poesie.",0,7,6); 
			base.books.AddGermanBookText("Dann schlagen ihm die Oger den Schädel ein, fressen ihn in einem eintägigen Gelage auf und bespannen mit seiner Haut eine Trommel.\nDie Oger fragen den Zwerg, ob er noch einen letzten",0,7,6); 
			base.books.AddGermanBookText("Wunsch hat. \"Ja, ich hätte gerne ein Messer!\" Die Oger beraten kurz und beschließen, dass der Zwerg auch mit Messer keine Gefahr ist.",0,7,6); 
			base.books.AddGermanBookText("Er bekommt sein Messer. Er packt es, rammt es sich fünfmal in den Bauch und brüllt: \"Aus mir macht ihr keine Trommel!\"",0,7,6); 
			base.books.AddGermanBookText("Ende",0,7,6);
			-----------------------------------------

 
        end
        base.books.SendBookPage(User,SourceItem.data,Counter);
    end
end  -- function

